@js:// 目录下一页规则
try {
    // 匹配第1/(\d+)关键词得到目录总页数
    var n = String(result).match(/第1[^\d]+(\d+)页/)[1];
    java.log(n);
    list = [];
    if (n == 1) {
        list;
    } else {
        for (i = 2; i <= n; i++) {
            turl = baseUrl.replace(/\.html/, "/");
            list.push(turl + i + ".html");
        }
    }
    list;
} catch (e) {}

