with(javaImport) {   
var big_c = new BigInteger("");
var big_b = new BigInteger("2");
var arry_d = java.base64DecodeToByteArray("rCXGfd2POMGzeiNIgo4iLg==");
var thisd;
function KeyPair_e(){
        var keyPairGenerator = KeyPairGenerator.getInstance("DH");
        var b = keyPairGenerator;
        var dHParameterSpec = new DHParameterSpec(big_c, big_b);
        var keyPairGenerator2 = b;
        if (keyPairGenerator2 == null) {
