function  keyPair2arry(keyPair) {
        var publicKey = keyPair.getPublic();
        if (publicKey == null) {
            return null;
        }
        var byteArray = publicKey.getY().toByteArray();
        return byteArray;
    }
function getZeroByteArray() {
        // 创建一个长度为16的字节数组
        var zeroByteArray = new Array(16);
        // 将数组中的每个元素初始化为0
        for (var i = 0; i < zeroByteArray.length; i++) {
