 function encode_a(bArr, bArr2, bArr3) {      
        var secretKeySpec = new SecretKeySpec(bArr, "AES");
        var ivParameterSpec = new IvParameterSpec(bArr2);
        var instance = Cipher.getInstance("AES/CBC/PKCS7Padding");
        instance.init(1, secretKeySpec, ivParameterSpec);
        var doFinal = instance.doFinal(bArr3);
        return doFinal;
    }

function encode_i(bArr, bArr2, bArr3) {
	/*
	java.log(JSON.stringify(bArr));
	java.log(JSON.stringify(bArr2));
	java.log(JSON.stringify(bArr3));
	*/
        var secretKeySpec = new SecretKeySpec(bArr, "AES");

        var ivParameterSpec = new IvParameterSpec(bArr2);

        var cipher = Cipher.getInstance("AES/CBC/PKCS7Padding");

        cipher.init(2, secretKeySpec, ivParameterSpec);
        var doFinal = cipher.doFinal(bArr3);
        return doFinal;
    }


 function Pair_y() {
        thisb = KeyPair_e();
        thisd = thisb;
        thisc = Random_a();
        var keyPair = thisb;
        if (keyPair == null) {
