            return null;
        }        
        var encode = Packages.cn.hutool.core.codec.Base64.encode(ArraysKtplus(bArr2, a3));
        return encode;
    }

function ArraysKtplus(bArr2, a3) {
    // 合并两个字节数组
    var combinedArray = new Array(bArr2.length + a3.length); 
    combinedArray = bArr2.concat(a3);
    return combinedArray; 
}

function sliceArray(array, start, end) {
  return array.slice(start, end);
}

  function encrypt_f(bArr, keyPair, bArr2, bArr3) {        
        var generatePublic = KeyFactory.getInstance("DH").generatePublic(
        	new DHPublicKeySpec(new BigInteger(1, bArr), big_c, big_b));      
        var keyAgreement = KeyAgreement.getInstance("DH");
        keyAgreement.init(keyPair.getPrivate());
        keyAgreement.doPhase(generatePublic, true);
        var generateSecret = keyAgreement.generateSecret();
        var length = generateSecret.length;
        var i = 0;

        for (var i2 = 0; i2 < length && generateSecret[i2] == 0; i2++) {
            i++;
        }

        var length2 = generateSecret.length;
        if (i + 31 >= length2) {
            i = length2 - 32;
        }
        let subArray;
 	       if (i >= 0 && i+31 <generateSecret.length) {
  subArray = sliceArray(generateSecret, i, i + 32);
  java.log('索引数组');
} else {
	subArray=null;
  java.log('索引超出数组界限');
}
        return encode_i(subArray, bArr3, bArr2);
    }

function x_f(str, str2, str3) {
        var z;    
        if (parseInt(str) == 1) {

            if (String(str2).length() > 0) {
                z = true;
            } else {
                z = false;
            }
            if (z) {
                var decode = android.util.Base64.decode(String(str3), 0);               

                let slice = decode.slice(0, 16);

                var decode2 = android.util.Base64.decode(String(str2), 0);

                var keyPair = thisd;
                if (keyPair == null) {
                	    java.log('keyPair is null');
                    return null;
                }
                var copyOfRange = Arrays.copyOfRange(decode, 16, decode.length);

                return new String(encrypt_f(decode2,keyPair,copyOfRange, slice),"UTF-8");
            }
            return str3;
        }
