ruleContent.content:
@js:
var reviewBox = java.getElements("#jieqi_page_contents .c_row.cf");
if(reviewBox.length == 0){
	result = "暂时还没有书评※=○☆(＿＿*)Ｚｚｚ";
}
else{
	var content = "";
	for(i = 0; i<reviewBox.length; i++){
		var name = reviewBox[i].select("p").text();
		var comment = reviewBox[i].select(".c_description").text();
		if(comment.endsWith("......全文")){
			var detailUrl = "https://www.youshu.me/" + reviewBox[i].select(".c_description a").attr("href");
			var doc = org.jsoup.Jsoup.parse(java.ajax(detailUrl));
			comment = doc.select("td.tdr div").last().text();
		}
		var stars = reviewBox[i].select("span.el-rate__icon.filled").text();
		var time = reviewBox[i].select("span.c_value")[1].text();
		var likes = reviewBox[i].select("span.ContentItem-actions span")[1].text();
		var dislikes = reviewBox[i].select("span.ContentItem-actions span")[3].text();
		content += `👤${name}:【${comment} 】\n${stars}\n👍(${likes}) 👎(${dislikes}) \n${time}\n————————————\n`;
	}
	result = content;
}

ruleToc.chapterList:
@js:
var aLast = java.getElements(".pagelink a.last");
var pageNum = Number(aLast.text());
var pageList = [];
var type;
try {
    var parsedObject = JSON.parse(source.getVariable());
    if (parsedObject && parsedObject.orderby !== undefined) {
        type = parsedObject.orderby;
    } else {
        type = "like";
    }
} catch (e) {
    type = "like";
}
var bookId = baseUrl.match(/\/reviews\/(\d+)/)[1];
if(type == "time"){
	for(i = 0; i<pageNum; i++){
		pageList.push({
			'name': "Page" + String(i+1),
			'url': `https://www.youshu.me/reviews/${bookId}/${String(i+1)}.html`
		})
	}
}
else{
	for(i = 0; i<pageNum; i++){
		pageList.push({
			'name': "Page" + String(i+1),
			'url': `https://www.youshu.me/modules/article/reviews.php?aid=${bookId}&type=like&page=${String(i+1)}`
		});
	}
}
pageList

exploreUrl:
@js:
var url = source.key,
html = java.ajax(url);
a = org.jsoup.Jsoup.parse(html)
.select("nav.nav li:gt(0):lt(12) a");
var arr = [], sort = [];
if (html && a) {
  arr = Array.from(a, x => ([
    x.text(), String(x.attr("href"))
    .replace(/1.?$/, "{{page}}/")
    .replace(/class.*(?!1).?$/,
      "$&<,{{page}}/>")
    .replace(/quanben.+(?!1).?$/,
      "$&<,{{page}}/>")]) );
  sort = arr.map(([title, link])=>({
    "title": title,
    "url": link,
    "style": {
      "layout_flexGrow": 1,
      "layout_flexBasisPercent": 0.2
    }
  }));

  JSON.stringify(sort);

} else java.toast("发现列表获取失败！");

