var [_, mid, pid] = src.split("#");
if (src.includes("chapter_image")) {
  var bmp = Packages.android.graphics.BitmapFactory.decodeByteArray(result,0,result.length);
  var path = src.split("?")[1].split(",")[0];
  mid = java.md5Encode16(path);
  cache.putMemory(mid, bmp);
  pid = "0";
}
if (mid && pid) {
  var ByteArrayOutputStream = Packages.java.io.ByteArrayOutputStream;
  var Bitmap = Packages.android.graphics.Bitmap;
  var bmp = cache.getFromMemory(mid);
  var width = bmp.getWidth();
  var height = bmp.getHeight();
  var count = Math.ceil(height / 1280);
  var page = parseInt(pid);
  var newHeight = 1280;
  if (height%1280 > 0 && page+2 > count) {
    newHeight = parseInt(height % 1280);
  }
  var newBmp = Bitmap.createBitmap(bmp, 0, page * 1280, width, newHeight, null, true);
  var byteStream = new ByteArrayOutputStream();
  newBmp.compress(Bitmap.CompressFormat.JPEG, 100, byteStream);
  result = byteStream.toByteArray();
  byteStream.close();
}
