function parse(html, ys, num) {
    let a = org.jsoup.Jsoup.parse(html);
    let b = Array.from(a.selectFirst(ys).attributes());
    return b[num - 1]?.toString().match(/"(.+)"/)?.[1];
}
(() => {
    let od, sd;
    x = Array.from(java.getElements("class.BCsectionTwo-top-chapter"));

    od = src.match(/originalOrder = (\[.+?\])/);
    sd = x.map((c) => {
        return parse(c, "li", 3);
    });
    if (od || sd) {
        if (od) {
            od = JSON.parse(od[1]).reduce((arr, item) => {
                arr.push(item + 1);
                return arr;
            }, []);
            x = x.map((item) => ({
                text:
                    item.select("a").attr("data-real") != ""
                        ? item.select("a").attr("data-real")
                        : item.select("a").text(),
                href: item.select("a").attr("href")
            }));
        } else if (sd) {
            x = x.map((item) => {
                _3 = parse(item, "li>a", 3);
                _4 = parse(item, "li>a", 4);
                text = isBase64(_3) === true ? _4 : _3;
                href = isBase64(_3) === true ? _3 : _4;
                return {
                    text: text,
                    href: java.base64Decode(href)
                };
            });
        }
        sot = od || sd;
        return x.sort((a, b) => {
            ia = x.indexOf(a);
            ib = x.indexOf(b);
            return sot[ia] - sot[ib];
        });
    }

    return x.map((item) => ({
        text: item.select("a").text(),
        href: item.select("a").attr("href")
    }));
})();

