let t = title.match(/(\d+)/);
t = t ? t[1] : null;

if (t) {
  let format = /第.+?章/;

  if (format.test(title)) {
    title = title.replace(/.*第\s*\d+\s*章\s*(\D*.*)/, "$1").replace(/\d+\s?/,"");
    title = `第${n2c(t)}章 ${title}`;
  } else {
    title = title.replace(/.*\d+[ .]+(\D*.*)/, "$1");
    title = `第${n2c(t)}章 ${title}`;
  }
} else {
  title;
}

