var url = java.ajax(bhost()).match(/window\.location\.href=\'([^']+)/)[1];
//java.log(url);
cookie.removeCookie(url);
var kku = url.replace(/m\/.*html$/,"m");
//java.log(kku);
var ckk = String(java.get(url,{}).body());
//java.log(ckk)
if (ckk.match(/Checking your browser before accessing/)) {
   java.webView(null, url, "document.cookie")
   cok = cookie.getCookie(url)
   head = JSON.stringify({ headers: { 'Cookie': cok } })
   u = url + "," + head
   java.ajax(u)
}
var result = kku + "/keywords-{{key}}-{{page}}.html"
result;

