result = requestApiUrl(`/${type}/catalog`, { novelId: novelId }, this)
let menus = Array.from(result.menus)

let cache = requestApiUrl(`/${type}/cache`, { novelId: novelId }, this) || []
cache = cache.reduce((acc, d) => {
  acc[d.chapId] = d
  return acc
}, {})

function formatTime(time) {
  return new Date(time).toLocaleString('zh-CN', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  })
}

function formatMenus(menus) {
  menus.forEach(c => {
    let cacheChap = cache[c.id]
    let isShare = !!cacheChap
    let tag = isShare ? 'ğŸ¥‘' : ''
    let info = ''
    let onlineTime = cacheChap ? cacheChap.onlineTime : formatTime(c.onlineTime)
    if (type == 'novel') {
      info = `${onlineTime} â”ƒ å­—æ•°ï¼š${c.volumeNum || 0}`
    } else if (type == 'comic') {
      info = `${onlineTime}${cacheChap ? ` â”ƒ å›¾ç‰‡æ•°ï¼š${cacheChap.imageCount || 0}` : ''}`
    } else {
      if (cacheChap) {
        info = `${onlineTime}${cacheChap.duration ? ` â”ƒ æ—¶é•¿ï¼š${cacheChap.durationFormat}` : ''}`
      } else {
        info = onlineTime
      }
    }
    let isVip = c.limit != 'æ¸¸å®¢' && c.limit != 'æ³¨å†Œä¼šå‘˜'
    chaps.push({
      title: isVip ? tag + c.title : c.title,
