ruleToc.chapterList:
@js:
let novelId = java.hexDecodeToString(result)
//java.log('ç›®å½•novelIdï¼š' + novelId)
function formatChapterLinks(htmlStr) {
  let aTagRegex = /<a\s+href="([^"]+)"[^>]*>([^<]+)<\/a>/g
  let result = []
  let match
  while ((match = aTagRegex.exec(htmlStr)) !== null) {
    let href = match[1]
    let chapName = match[2]
    let chapId = href.replace(/^\/book\//, '').replace(/\.html$/, '').replace('/', 'U')
    result.push({
      href: href,
      chapName: chapName,
      chapId: chapId
    })
  }
  return result
}

