@js:
try {
	// Aes解密
	matchResult = src.match(/html\(d\(['"](.+)['"],\s+['"](.+)['"]\)\);/);
	string = matchResult[1];
	code = java.md5Encode(matchResult[2]);
	iv = code.substring(0, 16);
	key = code.substring(16);
	result = java.createSymmetricCrypto("AES/CBC/PKCS7Padding", key, iv).decryptStr(string);
} catch (err) {
	//解密失败使用webView加载重新获取内容
	src = java.ajax(baseUrl + ',{"webView": true}');
	result = java.getString('#C0NTENT@html', src);
}
result

ruleToc.chapterList:
@js:
function isBase64(t){return"string"==typeof t&&0!==t.length&&/^(?:[A-Za-z0-9+/]{4})*(?:[A-Za-z0-9+/]{2}==|[A-Za-z0-9+/]{3}=)?$/.test(t)}
function parse(html, ys, num) {
    let a = org.jsoup.Jsoup.parse(html);
    let b = Array.from(a.selectFirst(ys).attributes());
    return b[num - 1]?.toString().match(/"(.+)"/)?.[1];
}
(() => {
    let od, sd;
    x = Array.from(java.getElements("class.BCsectionTwo-top-chapter"));

    od = src.match(/originalOrder = (\[.+?\])/);
    sd = x.map((c) => {
        return parse(c, "li", 3);
    });
    if (od || sd) {
        if (od) {
            od = JSON.parse(od[1]).reduce((arr, item) => {
                arr.push(item + 1);
                return arr;
            }, []);
            x = x.map((item) => ({
                title:
                    item.select("a").attr("data-real") != ""
                        ? item.select("a").attr("data-real")
                        : item.select("a").text(),
                href: item.select("a").attr("href")
            }));
        } else if (sd) {
            x = x.map((item) => {
                _3 = parse(item, "li>a", 3);
                _4 = parse(item, "li>a", 4);
                title = isBase64(_3) === true ? _4 : _3;
                href = isBase64(_3) === true ? _3 : _4;
                return {
                    title: title,
                    href: java.base64Decode(href)
                };
            });
        }
        sot = od || sd;
        return x.sort((a, b) => {
            ia = x.indexOf(a);
            ib = x.indexOf(b);
            return sot[ia] - sot[ib];
        });
    }

    return x.map((item) => ({
        title: item.select("a").text(),
        href: item.select("a").attr("href")
    }));
})();

