<js>
list = result.toArray();
s = /originalOrder = .*?;/.test(src)?eval(src.match(/originalOrder = (.*?);/)[1]):result;
l = [];
if(/<li.*?"\d+" .*?-[^"]+="\d+">/.test(result)){
	num = String(result).match(/<li.*?"\d+" (.*?-[^"]+)="\d+">/)[1];
	l = list.sort((a,b)=>a.attr(num)-b.attr(num));
	}else{
for(i in list){ l[s[i]] = list[i]};
}
l.join("")
