@js:
const sort=[];
const base="/Service";
const styleTitle={layout_flexGrow:1,layout_flexBasisPercent:1
};
const styleItem={layout_flexGrow:0,layout_flexBasisPercent:0.29};
const states=[
	{title:"完本",value:1},
	{title:"连载",value:0}
];
const sortRules=[
	{title:"入库",value:0},
	{title:"热门",value:20},
	{title:"点击",value:4},
	{title:"更新",value:12},
	{title:"收藏",value:14}
];
const categories=[
	//["仙侠","xianxia"],
	//["网游","wangyou"],
	//["惊悚","jingsong"],
	//["灵异","lingyi"],
	//["言情","yanqing"],
	//["耽美","danmei"],
	//["女尊","nvzun"],
	["都市","dushi"],
	["乡土","xiangtu"],
	["官场","guanchang"],
	["校园","xiaoyuan"],
	["异能","yineng"],
	["历史","lishi"],
	["古典","gudian"],
	["武侠","wuxia"],
	["穿越","chuanyue"],
	["同人","tongren"],
	["单篇","danpian"]
];
function buildUrl(state,sortValue,parentclassid){
	const params=`{\\"Method\\":\\"book_byshuyuansearch\\",\\"Parameters\\":{\\"index\\":\\"{{page-1}}\\",\\"state\\":${state},\\"sort\\":${sortValue},\\"parentclassid\\":\\"${parentclassid}\\"}}`;
	return `${base},{"body":"requestData=${params}","method":"POST"}`;
};
sortRules.forEach(rule=>{
	sort.push({
		title:rule.title,
		url:buildUrl(0,rule.value,0),
		style:styleItem
	});
});
states.forEach(state=>{
	sort.push({
		title:`🌀${state.title}🌀`,
		url:"",
		style:styleTitle
	});
	sortRules.slice(0,3).forEach(sortRule=>{
		sort.push({
			title:`${sortRule.title}`,
			url:buildUrl(state.value,sortRule.value,0),
			style:styleTitle
		});
		categories.forEach(([title,uri])=>{
			sort.push({
				title:title,
				url:buildUrl(state.value,sortRule.value,uri),
				style:styleItem
			});
		});
	});
});
//java.log(JSON.stringify(sort));
JSON.stringify(sort)

@js:
sort =[];
function push(title,url,type1,type2){
	var item ={title:title,url:url};
	if (type1!==undefined&&type2!==undefined){
		item.style={
			layout_flexGrow:type1,
			layout_flexBasisPercent:type2
		}
	}
	sort.push(item)
};
const page="/{{page}}.html";
const groups=[{
	prefix:"❶",
	types:["更新","点击"],
	typeUrls:["/fictions","/fictions/sort-read"]
}];
const tags=[
	"编辑推荐","人妻女友","学生校园","伴侣交换","都市生活",
	"动漫游戏","名人明星","经验故事","古典玄幻","家庭乱伦",
	"多人群交","公司职场","露出暴露","强暴性虐","西方主题",
	"同性主题","绿帽主题","长篇连载","经典回忆","有声小说"
];
const lengths=[{
	name:"短篇",
	suffix:"/length-1"
},{
	name:"中篇",
	suffix:"/length-2"
},{
	name:"长篇",
	suffix:"/length-3"
}];
groups.forEach(group=>{
	group.types.forEach((type,typeIndex)=>{
		const typeUrl=group.typeUrls[typeIndex];
		push(group.prefix+type,typeUrl+page,1,1);
		lengths.forEach(len=>{
			const lenUrl=typeUrl+len.suffix;
			push("❷"+len.name,lenUrl+page,1,1);
			tags.forEach(tag=>{
				const tagUrl=typeUrl+"/tag-"+tag+len.suffix;
				push(tag,tagUrl+page);
			});
		});
	});
});
JSON.stringify(sort)

