searchUrl:
@js:
const sign_key = "d3dGiJc651gSQ8w1";
const action = "/api/v7/search/words?";

const headers = {
    "app-version": "51110",
    "platform": "android",
    "reg": "0",
    "AUTHORIZATION": "",
    "application-id": "com.****.reader",
    "net-env": "1",
    "channel": 'unknown',
    "qm-params": ""
};

var params = {
    "gender": "0",
    "imei_ip": "3076332568",
    "page": page,
    "wd": key
};

var urlEncode = function(param, key, encode) {
    if (param == null) return "";
    var paramStr = "";
    var t = typeof(param);
    if (t == "string" || t == "number" || t == "boolean") {
        paramStr += "&" + key + "=" + ((encode == null || encode) ? encodeURIComponent(param) : param);
    } else {
        for (var i in param) {
            var k = key == null ? i : key + (param instanceof Array ? "[" + i + "]" : "." + i);
            paramStr += urlEncode(param[i], k, encode);
        }
    }
    return paramStr;
};

var headerSign = String(java.md5Encode(
    Object.keys(headers).sort().reduce((pre, n) => pre + n + "=" + headers[n], "") + sign_key
));

var paramSign = String(java.md5Encode(
    Object.keys(params).sort().reduce((pre, n) => pre + n + "=" + params[n], '') + sign_key
));

headers["sign"] = headerSign;
params["sign"] = paramSign;
body = urlEncode(params);

var search_url = action + body + "," + java.put("headers", JSON.stringify({"headers": headers}))

search_url

