const hosts = [
    "https://www.qbmfxs.com",
    "https://www.1qxs.com",
    "https://www.2vdu.com",
    "https://www.0xs.net",
    "https://www.goboo.cc"
];

function testHost()
{
	const { source } = this;
	let v = source.getVariable();
	if((!v.match(/\d+/)) || v < 0 || v >= hosts.length)
	{
		source.setVariable(0);
	}
}

function getHost()
{
	const { source } = this;
	let v = source.getVariable();
	let index = 0;
	if((!v.match(/\d+/)) || v < 0 || v >= hosts.length)
	{
		source.setVariable(index);
	}
	else
	{
		index = v;
	}
	return hosts[index];
}

