        if (varString=="") {
            java.toast("当前未设置来源，使用默认来源");
            var baseVariable = JSON.stringify({
	                     sourceType: "first",
	                     sourceName: "🐈猫眼①",
	                    	sourceAPI: "http://api.myweipin.com"
             }, null, "\t");
            source.setVariable(baseVariable);
            return;
        }

       let currentData = JSON.parse(varString);
        let currentSource = "未设置";

        for (let i = 0; i < data.length; i++) {
            if (data[i][1] === currentData.sourceType) {
                currentSource = data[i][0];
                break;
            }
        }

        java.toast(`当前来源: ${currentSource}`);
    } catch (e) {
        java.toast("模式数据解析错误: " + e.message);
        java.log("解析源变量出错: " + e);
    }
}
