        let validSource = false;
        let sourceName = "";
        let sourceAPI = "";

        for (let i = 0; i < data.length; i++) {
            if (data[i][1] === sourceType) {
                validSource = true;
                sourceName = data[i][0];
                sourceAPI = data[i][2];
                break;
            }
        }

        if (validSource) {
            var variable = {
                sourceType: sourceType,
                sourceName: sourceName,
                sourceAPI: sourceAPI
            };

            var jsonStr = JSON.stringify(variable, null, "\t");
            source.setVariable(jsonStr);
            java.toast(`✅ 已切换到: ${sourceName}`);
            java.log("设置源变量: " + jsonStr);
        } else {
            java.toast("❌ 无效来源");
        }
    } catch (e) {
        java.toast("切换来源出错: " + e.message);
        java.log("切换来源出错: " + e);
    }
}

