function time() {
java.toast("正在检测更新，时间可能会有点长，请耐心等待！");
var api = [
    "http://api.myweipin.com",
    "http://api.lfdapengu.com",
    "http://api.jmlldsc.com",
    "http://api.lemiyigou.com"
];

var searchPath = "/search?keyword=开局圣人修为，女帝成了前妻&page=1";

var results = [];
var fastestApi = null;
var minTime = Infinity;

for (var i = 0; i < api.length; i++) {
    var apiUrl = api[i] + searchPath;
    var startTime = new Date().getTime();

    try {
        var response = java.ajax({
            url: apiUrl,
            method: "GET",
            timeout: 5000
        });

        var endTime = new Date().getTime();
        var responseTime = endTime - startTime;

        results.push({
            api: api[i],
            time: responseTime,
            status: "成功",
            success: true
        });

        if (responseTime < minTime) {
            minTime = responseTime;
            fastestApi = api[i];
        }

        //java.log("API 测试: " + apiUrl + " | 耗时: " + responseTime + "ms");

    } catch (e) {
        var endTime = new Date().getTime();
        var responseTime = endTime - startTime;

        results.push({
            api: api[i],
            time: responseTime,
            status: "失败: " + e.message,
            success: false
        });

        java.log("API 测试失败: " + apiUrl + " | 错误: " + e.message);
    }
}
var report = "=== API 响应时间测试报告 ===\n\n";
report += "检验关键词: 开局圣人修为，女帝成了前妻\n\n";

for (var j = 0; j < results.length; j++) {
    var result = results[j];
    report += "API " + (j + 1) + ": " + result.api + "\n";
    report += "状态: " + result.status + "\n";
    report += "响应时间: " + result.time + "ms\n";
    report += "--------------------------------\n";
}

if (fastestApi) {
    report += "\n✅ 最快 API: " + fastestApi + " (" + minTime + "ms)\n";
    report += "推荐使用此接口进行后续请求\n       已打印至日志";
} else {
    report += "\n⚠️ 所有 API 测试均失败，请检查网络连接或 API 状态";
}
java.log(report);
java.longToast(report);
}

function setHost() {
    try {
        let loginInfo = source.getLoginInfoMap();
        if (!loginInfo) loginInfo = {};

        let customApi = loginInfo["自定义服务器"] || "";

        if (!customApi) {
            java.toast("请先设置自定义服务器地址");
            return;
        }

        var variable = {
            sourceType: "custom",
            sourceName: "自定义服务器",
            sourceAPI: customApi
        };

        var jsonStr = JSON.stringify(variable);
        source.setVariable(jsonStr);
        java.toast("✅ 已使用自定义服务器: " + customApi);
    } catch (e) {
        java.toast("设置自定义服务器出错: " + e.message);
    }
}
