@js:
var result = [];
java.toast("🔥正在刷新列表,请稍等！");
var push = (title, url, size) => result.push({
  "title": title,
  "url": url,
  "style": {
    "layout_flexGrow": 1,
    "layout_flexBasisPercent": size
  }
});

var fl = java.ajax("https://www.ikxsw.org/sort/");
var wb = java.ajax("https://www.ikxsw.org/quanben/sort/");
var ph = java.ajax("https://www.ikxsw.org/rank/");

if (fl && wb && ph) {

  a = org.jsoup.Jsoup.parse(fl).select(".type-fen-1-1 a");
  b = org.jsoup.Jsoup.parse(wb).select(".type-fen-1-1 a");
  c = org.jsoup.Jsoup.parse(ph).select(".type-fen-1-1 a");

  push("🔥分类小说🔥", null, 1);
  for (var i = 0; i < a.length; i++) {
    title = a[i].text();
    url = a[i].attr("href");
    size = 0.33;
    title = String(title).replace(/\s/g, "");
    url = baseUrl + String(url).replace(/\d+\/$/, "") + "{{page}}/";
    push(title, url, size);
  };

  push("🔥完本小说🔥", null, 1);
  for (var i = 0; i < b.length; i++) {
    title = b[i].text();
    url = b[i].attr("href");
    size = 0.33;
    title = String(title).replace(/\s/g, "");
    url = baseUrl + String(url).replace(/\d+\/$/, "")+"{{page}}/";
    push(title, url, size);
  };

  push("🔥排行榜单🔥", null, 1);
  for (var i = 0; i < c.length; i++) {
    title = c[i].text();
    url = c[i].attr("href");
    size = 0.33;
    title = String(title).replace(/\s/g, "");
    url = baseUrl + String(url).replace(/\d+\/$/, "");
    push(title, url, size);
  };

} else {
  java.toast("🤔列表刷新失败！！！");
}

JSON.stringify(result);

