const localVersion = '3.6.15';


function login() {
    let zdyserver;
    try {
        zdyserver = String(source.getLoginInfoMap()['自定义服务器(可不填)']);
        if (zdyserver.includes('http')) {
            setArgument('server', zdyserver);
            java.toast(`\n\n当前服务器为自定义服务器\n${zdyserver}\n\n切换服务器请先清空服务器地址中的数据`);
        } 
            } catch (error) {
    }
    let zdytone_id;
    try {
        zdytone_id = String(source.getLoginInfoMap()['听书Ai音色填写后点击右上角✔']);
        if (zdytone_id) {
            setArgument('tone_id', zdytone_id);
        } else {
            setArgument('tone_id', '默认音色');
        }
    } catch (error) {
    }
    let sq_user_id;
    try {
        sq_user_id = String(source.getLoginInfoMap()['书旗id(可不填)']);
        if (sq_user_id) {
            setArgument('sq_user_id', sq_user_id);
        } else {
            setArgument('sq_user_id', '0');
        }
    } catch (error) {
    }
}

function qtsj() {
	java.startBrowserAwait(getArgument('server') + '/online_bookshelf','大灰狼书架');
	}

// 填写密钥
function loginqt() {
        java.startBrowserAwait(getArgument('server') + '/login', '登录大灰狼小说书源');
    }

//登录番茄
function fq_login() {
    var cookies = cookie.getCookie('fanqienovel.com') || java.getCookie('fanqienovel.com')
    java.log(cookies);
    if (cookies && cookies != "") {
        java.toast("请不要重复登录，请先退出登录！")
        return false
    }
    try {
        java.startBrowserAwait("https://fanqienovel.com/", "登录")
    } catch (e) {
        java.toast(e)
    }
    try {
        cookie.removeCookie("snssdk.com")
    } catch (e) {
    }
    var cookies = cookie.getCookie("fanqienovel.com")
    if (!cookies || cookies == "") {
        java.toast("登录失败！")
        return false
    }
    java.toast("登录成功！")
    return true
}

//退出登录
function logout() {
    cookie.removeCookie("fanqienovel.com");
    cookie.removeCookie("snssdk.com");

    const servers = host
    for (let server of servers) {
    cookie.removeCookie(server);
    }
    java.toast("退出登录成功");
}

//获取参数
function getArgument(key) {
    let open_argument = source.getVariable();
    open_argument = getArguments(open_argument, '');
    return open_argument[key];
}

//设置参数
function setArgument(key, value) {
    let open_argument = source.getVariable();
    open_argument = getArguments(open_argument, '');
    open_argument[key] = value;
    open_argument = JSON.stringify(open_argument);
    source.setVariable(open_argument);
    return open_argument;
}

//设置男女频
function set_source_type(source_type) {
	  setArgument("source_type", source_type);
	  java.toast("\n发现页已设置为:"+source_type);
	}

//首页
function api() { java.startBrowserAwait('http://fb.langge.cf', "首页");
}

//打赏
function vip() {
    java.startBrowserAwait(getArgument('server') + '/coffee', "喝咖啡");
}

//获取密钥
function hqmy() {
    java.startBrowserAwait(getArgument('server') + '/key', "获取密钥");
}

var server = getArgument('server');

//设置搜索媒体
function set_media(media) {
    const mediaConfig = {
        '喜马拉雅': ['听书'],
        '番茄': '*',
        '如漫画':['漫画'],
        '福利漫画':['漫画'],
        '漫画屋':['漫画'], 
        '漫画网':['漫画'],        
        '酷狗听书':['听书'],
        '海洋听书':['听书'],
        '七猫': ['小说', '听书', '短剧'],
        '河马': ['短剧'],
        '毒舌影视':['短剧'],
        '歪瑞古德': ['漫画'],
        '全部': '*', // 允许所有模式
        '默认': ['小说']
    };

    const source = getArgument('source');
    const allowedModes = mediaConfig[source] || mediaConfig['默认'];
    let targetMedia = mediaConfig['默认'][0]; 
    let isAllowed = false;

    if (allowedModes === '*' || allowedModes.includes(media)) {
        targetMedia = media;
        isAllowed = true;
    } else if (Array.isArray(allowedModes)) {
        targetMedia = allowedModes[0]; 
    }

    const message = isAllowed 
        ? `\n\n已切换至：${targetMedia}\n请重新搜索书籍！`
        : `\n\n目前${source}：不支持【${media}】模式！\n已自动切换至：${targetMedia}`;

    setArgument('media', targetMedia);
    java.toast(message);
}

//获取搜索媒体
function get_media() {
    let media = getArgument('media');
    let source = getArgument('source');
    if (media == '') {
        media = '全部';
    }
    java.toast(`\n\n当前使用源：${source}-${media}`);

}

//获取服务器
function get_server() {
    var server = getArgument('server');
    var tishi = '\n\n当前服务器：' + server
    java.toast(tishi);
    return server
}


//设置服务器
function set_server() {
    let zdyserver;
    try {
        zdyserver = String(source.getLoginInfoMap()['自定义服务器(可不填)']);
        if (zdyserver.includes('http')) {
            setArgument('server', zdyserver);
            java.toast(`\n\n当前服务器为自定义服务器\n${zdyserver}\n\n切换服务器请先清空服务器地址中的数据`);
        } else {
            zdyserver = '';
        }
    } catch (error) {
        zdyserver = '';
    }
    if (!zdyserver) {
        const servers = host
        const currentServer = getArgument('server') || '';
        const currentIndex = servers.indexOf(currentServer);

        const nextIndex = currentIndex >= 0 ? (currentIndex + 1) % servers.length : 0;
        const nextServer = servers[nextIndex];

        setArgument('server', nextServer);
        java.toast(`\n\n当前服务器${nextIndex+1}:${nextServer}\n\n注意：切换服务器后需要在详情页刷新目录才能生效`);
    }
}

//获取音色
var tone_id = getArgument('tone_id');

function get_tone_id(arg) {
    var datadist = {
        "0": "默认音色",
        "-1": "阅读模式",
        "-2": "漫画模式",
        "51": "多人发音",
        "1": "甜美少女",
        "2": "清亮青叔",
        "5": "开朗青年",
        "6": "温柔淑女",
        "4": "成熟大叔",
        "74": "大叔升级",
        "30": "优雅御姐"
    };
    var tone_id = datadist[arg] || arg;
    var tishi = '\n\n当前音色：' + tone_id;
    java.toast(tishi);
}

//设置音色
function set_tone_id(mode, name) {
    let zdytone_id;
    try {
        zdytone_id = String(source.getLoginInfoMap()['其他音色填写后点击右上角✔']);
        if (zdytone_id) {
            setArgument('tone_id', zdytone_id);
            java.toast(`\n\n当前音色为自定义音色\n${zdytone_id}\n\n切换音色请先清空音色输入框中的数据`);
        } else {
            zdytone_id = '';
        }
    } catch (error) {
        zdytone_id = '';
    }
    if (!zdytone_id) {
        let toast = "\n\n已切换至：" + name + '\n\n切换后需要刷新详情页';
        setArgument('tone_id', mode);
        java.toast(toast);
    }
