//设置搜索媒体
function set_media(media) {
    const mediaConfig = {
        '喜马拉雅': ['听书'],
        '番茄': '*',
        '福利':['小说'],
        '如漫画':['漫画'],
        '包子漫画':['漫画'],
        '九妖漫画':['漫画'],
        '绅士漫画':['漫画'],
        '六月听书':['听书'],
        '海洋听书':['听书'],
        '七猫': ['小说', '听书'],
        '河马': ['短剧'],
        '歪瑞古德': ['漫画'],
        '毒舌影视':['短剧'],
        '全部': '*', // 允许所有模式
        '默认': ['小说']
    };

    const source = getArgument('source');
    const allowedModes = mediaConfig[source] || mediaConfig['默认'];
    let targetMedia = mediaConfig['默认'][0]; 
    let isAllowed = false;

    if (allowedModes === '*' || allowedModes.includes(media)) {
        targetMedia = media;
        isAllowed = true;
    } else if (Array.isArray(allowedModes)) {
        targetMedia = allowedModes[0]; 
    }

    const message = isAllowed 
        ? `\n\n已切换至：${targetMedia}\n请重新搜索书籍！`
        : `\n\n目前${source}：不支持【${media}】模式！\n已自动切换至：${targetMedia}`;

    setArgument('media', targetMedia);
    java.toast(message);
}

//获取搜索媒体
function get_media() {
    let media = getArgument('media');
    let source = getArgument('source');
    if (media == '') {
        media = '全部';
    }
    java.toast(`\n\n当前使用源：${source}-${media}`);

}

//获取服务器
function get_server() {
    var server = getArgument('server');
    var tishi = '\n\n当前服务器：' + server
    java.toast(tishi);
    return server
}


//设置服务器
function set_server() {
    let zdyserver;
    try {
        zdyserver = String(source.getLoginInfoMap()['自定义服务器(可不填)']);
        if (zdyserver.includes('http')) {
            setArgument('server', zdyserver);
            java.toast(`\n\n当前服务器为自定义服务器\n${zdyserver}\n\n切换服务器请先清空服务器地址中的数据`);
        } else {
            zdyserver = '';
        }
    } catch (error) {
        zdyserver = '';
    }
    if (!zdyserver) {
        const servers = host
        const currentServer = getArgument('server') || '';
        const currentIndex = servers.indexOf(currentServer);

        const nextIndex = currentIndex >= 0 ? (currentIndex + 1) % servers.length : 0;
        const nextServer = servers[nextIndex];

        setArgument('server', nextServer);
        java.toast(`\n\n当前服务器${nextIndex+1}:${nextServer}\n\n注意：切换服务器后需要在详情页刷新目录才能生效`);
    }
}

