ruleToc.chapterList:
@js:
let a = java.getElements('#list-chapterAll dd a'),
list = [];
for (let i = 0; i < a.length; i++) 
{
    try 
    {
        let text = a[i].text(),
        href = a[i].attr('href');
        if (i > 1) 
        {
            if (href == a[i - 1].attr('href')) 
            {
                let num1 = parseInt(a[i - 1].attr('href').match(/\d+/)[0]);
                num2 = parseInt(a[i - 2].attr('href').match(/\d+/)[0]),
                jg = num1 - num2,
                cNum = parseInt(href.match(/\d+/)[0]);
                if (i < a.length - 1) {
                    href == a[i + 1].attr('href') ? href = (cNum + 1) + '.html' : href = (cNum + jg) + '.html';
                }
                else {
                    href = (cNum + jg) + '.html';
                }
            }
            list.push({
                text, href 
            });
        }
        else {
            list.push({
                text, href 
            });
        }
    }
    catch (e) {
        java.log(e);
        java.toast('\n'+e);
    }
}
list;

ruleContent.content:
$.data
<js>
function cleanChapterText(text) {
  const lines = text.split('\n');
  const filteredLines = lines.filter(line => {
    return !(line.includes('第') && line.includes('章')) && 
           !line.includes('作者：') &&
           !/^\d{4}-\d{2}-\d{2}/.test(line.trim());
  });
  return filteredLines.join('\n');
}

cleanChapterText(result);
</js>

