@js:
classList=[];
push=(title,url,type1,type2)=>classList.push({
	title:title,
	url:url,
	style:{
		layout_flexGrow:type1,
		layout_flexBasisPercent:type2
	}
});
push('🔔更换排序后刷新我了吗','',1,1);

var initialType=JSON.stringify({
	'orderby':'like',
	'order':'allvisit',
	'num':0,
	'state':0,
	'platform':0
});

var type=source.getVariable()==''?initialType:source.getVariable();
var typeParsed=JSON.parse(type);

male=["全部","玄幻","奇幻","武侠","仙侠","都市","现实","军事","历史","悬疑","游戏","竞技","科幻","灵异","二次元","同人"];
push('男频',null,1,1);
male.map((title,index)=>{
	index++;
	url=`https://m.youshu.me/book_${typeParsed.order}_1_${index-1}_${typeParsed.num}_0_${typeParsed.state}_${typeParsed.platform}_{{page}}.html,{"headers":{"User-Agent":"Symbian"}}`;
	push(title,url,1,0.2);
});

female=["全部","穿越时空","架空历史","总裁豪门","都市言情","仙侠奇缘","幻想言情","悬疑推理","耽美纯爱","衍生同人","轻小说","综合其它"];
push('女频',null,1,1);
female.map((title,index)=>{
	if (index==0){
		url=`https://m.youshu.me/book_${typeParsed.order}_2_0_${typeParsed.num}_0_${typeParsed.state}_${typeParsed.platform}_{{page}}.html,{"headers":{"User-Agent":"Symbian"}}`;
	}else{
		url=`https://m.youshu.me/book_${typeParsed.order}_2_${index+16}_${typeParsed.num}_0_${typeParsed.state}_${typeParsed.platform}_{{page}}.html,{"headers":{"User-Agent":"Symbian"}}`;
	}
	index++;
	push(title,url,1,0.3);
});
JSON.stringify(classList)

