  /\.icon-(\d+):before\s*\{\s*content\s*:\s*"\\e([0-9a-fA-F]{3,4})"\s*;\s*\}/g,
  function(_, id, hexDigits) {
    var className = 'icon-' + id;
    var char = String.fromCharCode(parseInt(hexDigits, 16));
    glyphMap[className] = char;
  }
);

rec = result.replace(
