exploreUrl:
@js:
java.toast("稍等，正在更新发现列表！");
var doc = org.jsoup.Jsoup.parse(java.ajax(source.bookSourceUrl + "/sort.html"));
var html = doc.select("div.content > ul > li.prev");
//java.log(html);

var sort = [];
sort.push({
   title: '💮      ' + source.bookSourceName + '      💮',
   url: '',
   style: {
       layout_flexGrow: 0,
       layout_flexBasisPercent: 1
   }
 })
for ( var i = 0; i < html.size(); i ++ ) { 
	    var li = html[i];

     var title = li.select("li > a[href]")[0].text();
     var herf = li.select("li > a[href]")[0].attr("href");
     var url = source.bookSourceUrl + String(herf).replace(/1(?=[^1]*$)/, "{{page}}");

     sort.push({
        title: '    ' + title + '    ',
        url: url,
        style: {
            layout_flexGrow: 1,
            layout_flexBasisPercent: 0.29
        }
    });
}
var docc = org.jsoup.Jsoup.parse(java.ajax(source.bookSourceUrl + "/top.html"));
var htmll = docc.select("div.content > ul > li.prev");
//java.log(htmll);
sort.push({
   title: '💮      排行榜      💮',
   url: '',
   style: {
       layout_flexGrow: 0,
       layout_flexBasisPercent: 1
   }
 })
for ( var p = 0; p < htmll.size(); p ++ ) { 
	    var lii = htmll[i];

     var titlee = lii.select("li > a[href]")[0].text();
     var herff = lii.select("li > a[href]")[0].attr("href");
     var urll = source.bookSourceUrl + String(herff).replace(/1/, "{{page}}");

     sort.push({
        title: '    ' + titlee + '    ',
        url: urll,
        style: {
            layout_flexGrow: 1,
            layout_flexBasisPercent: 0.29
        }
    });
}
JSON.stringify(sort);

