function webLogin() {
    var ck = String(cookie.getKey("fanqienovel.com", "sessionid"));
    if (ck && ck != "") {
        java.toast("请不要重复登录，请先退出登录！")
        return false
    } 
    try {
            java.startBrowserAwait("https://fanqienovel.com/", "登录")
    } catch (e) { java.toast(e) }

    try {
        cookie.removeCookie("snssdk.com")
    } catch (e) {}
    var ck = "sessionid=" + String(cookie.getKey("fanqienovel.com", "sessionid"));
    let user
    try {
      user = JSON.parse(java.ajax("https://fanqienovel.com/api/user/info/v2," + JSON.stringify({
        method: "GET",
        headers: {
          "Cookie": ck
        }
      }))).data.name
     } catch (e) {java.log(e)}
    if (!ck || ck == "sessionid=" || !user) {
        java.toast("未获取到登录凭据，登录失败")
        cookie.removeCookie("fanqienovel.com")
        return false
    }
    java.toast("欢迎 " + user + "\n登录成功！")
    return true
}

function login() {}

function logOut() {
    cookie.removeCookie("fanqienovel.com");
    java.toast("退出成功");
}

