    return url.match(/\/page\/(\d+)/)?.[1] || url.match(/book_id=(\d+)/)?.[1];
}

if (url.startsWith('legadosearch://')) {
    java.searchBook(decodeURIComponent(url.replace('legadosearch://', '')));
}

let needBreak = false;
function isNewLegado() {
    try {
        return !!Packages.io.legado.app.ui.book.manga;
    } catch(e) {
        return false; 
    }
}

if (isNewLegado()) {
    const bookId = extractBookId(url);
    if (bookId) {
        const apiUrl = `https://api5-normal-sinfonlineb.fqnovel.com/reading/bookapi/multi-detail/v/?aid=1967&iid=1&version_code=999&book_id=${bookId}`;
