    Type = /\d+/.test(Map('分类：')) ? Map('分类：').replace(/，/g, ',') : '0,1,2,3,4';
    index = Type.split(",");
    types = [{'name':'新番放送','id':'20'},{'name':'追番计划','id':'4'},{'name':'动漫剧场','id':'3'},{'name':'国产动漫','id':'22'},{'name':'欧美动漫','id':'21'}];
    tags = ['更新','新片','日榜','周榜','月榜'];
    alls = ['时间排序','人气排序','评分排序'];
    by = ['/by/time','/by/hits','/by/score'];
    for (i in index) {
        exploreUrl.push(explore('分类', types[index[i]].name, types[index[i]].id));
        for (j in tags) {
            exploreUrl.push(explore('分类首页', tags[j], types[index[i]].id));
        }
        for (k in alls) {
            exploreUrl.push(explore('分类片库', alls[k], types[index[i]].id, by[k]));
        }
    }

    var url = Get('url');
    var html = java.webView('', url, '');
    if(String(html).includes('no-js')){
        cookie.removeCookie(Get('url'));
        html = java.startBrowserAwait(url,'验证').body();
        ck = cookie.getCookie(Get('url'));
        cache.put(Get('url') + 'ck',ck);
    }

    `搜索::${Get('url')}/@js:\`{{Get('url')}}/index.php/vod/search/page/{{page}}/wd/{{Skey('搜索：')}}.html\`\n
${exploreUrl.join('\n')}`
}

