// 页面操作
function updateUrlParam(key, value) {
    const url = new URL(currentUrl);
    url.searchParams.set(key, value);
    url.searchParams.set('offset', 0);
    currentOffset = 0;
    updateCurrentPage()
    fetchAndRender(url.toString());
}

function updateOffset(delta) {
    const url = new URL(currentUrl);
    const newOffset = Math.max(0, currentOffset + delta);
    currentOffset = newOffset;
    url.searchParams.set('offset', newOffset);
    fetchAndRender(url.toString());
}

function jumpToPage() {
    const pageInput = document.querySelector('.page-input');
    const page = parseInt(pageInput.value);
    if (isNaN(page) || page < 1 || page > totalPages) {
        alert(`请输入有效的页码(1-${totalPages})`);
        return;
    }

    currentOffset = (page - 1) * 20;
    const url = new URL(currentUrl);
    url.searchParams.set('offset', currentOffset);
    fetchAndRender(url.toString());
}

function updateCurrentPage() {
    const books = document.querySelectorAll('.book');
    if (books.length === 0) return;

    // 使用IntersectionObserver检测可见书籍
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const bookOffsetAttr = entry.target.getAttribute('data-offset');

const bookOffset = bookOffsetAttr !== null ? parseInt(bookOffsetAttr) : currentOffset;

                const currentPage = Math.floor(bookOffset / 20) + 1;

                // 只有当页码确实改变时才更新DOM
                const currentDisplay = document.querySelector('.current-page').textContent;
                if (currentDisplay !== currentPage.toString()) {
                    document.querySelector('.current-page').textContent = currentPage;
                    document.querySelector('.current').textContent = currentPage;
                    currentOffset = bookOffset;
                }
            }
        });
    }, {
        threshold: 0.5 // 当书籍50%可见时触发
    });

    // 观察所有书籍
    books.forEach(book => {
        observer.observe(book);
    });

    // 清理观察器
    return () => {
        observer.disconnect();
    };
}


