      size = window.innerHeight / unit;
      unit = "vh"
    }

    const pubStyle = `background-color: rgb(255, 153, 0);border-radius:5px;border: 1px solid rgb(255, 153, 0);color: #fff;line-height:1;font-size:${4 * size}${unit};font-weight: 400;padding: ${size}${unit} ${size}${unit};`;

    const pubBtn = (btns) => {
      let text = "";
      for (let i in btns) {
        text += `<button type="button" style="margin:5px auto;${pubStyle}" onclick="window.open(\`legado://import/addToBookshelf?src=${encodeURIComponent(btns[i])},{origin:'通用书源'}\`, '_blank')">${i}</button>`
      }
      return text
    };

    btn.innerHTML = `<button type="button" id='open-yuedu-bookshelf' style="z-index: 9999999999;position: fixed;bottom: 7%;right: 7%;${pubStyle}">加入书架</button>`;

    autoBtn.innerHTML = `<div id='auto-yuedu-bookshelf' style="background-color:#ffffff;z-index: 99999999999;position: fixed;bottom:0;left: 0;width:100%;">
  <div style="background-color:#f0f0f0;border-top:1px rgb(255, 153, 0) solid;display:flex;flex-wrap: wrap;">${pubBtn({
      目录识别: window.location.href,
      尾页目录: `${window.location.href}?录`,
      单页识别: `${window.location.href}?单`,
      尾页正文: `${window.location.href}?[${window.location.href}]单`,
    })}
  </div>
  <div style="padding:0px 5px;border-top: 1px solid rgb(255, 153, 0);">
    <p><span style="color:rgb(255, 153, 0);">目录识别: </span>当前为目录页，且所有章节都在此页或有下一页按钮时，点"目录识别"按钮自动识别</p>
    <p><span style="color:rgb(255, 153, 0);">尾页目录: </span>目录页无下一页按钮且存在其他目录页时，进入最后一个目录页，点"尾页目录"逆推生成全部目录（此方式生成的目录无法更新）
    </p>
    <p><span style="color:rgb(255, 153, 0);">单页识别:
      </span></p>
    <p>1. 当前为正文且页面下方为纯数字形式的目录时，点"单页识别"按钮自动识别</p>
    <p>2. 当前为正文且当前书籍无录页但有下一页按钮时，点"单页识别"按钮自动识别（目录过多时需要的识别时间比较长，可考虑进入最后一个正文页点"尾页正文"按钮快速逆推生成整本书）</p>
    <p><span style="color:rgb(255, 153, 0);">尾页正文:
      </span>章节链接符合数字递增规律时，进入最后一个正文页，点"尾页正文"逆推生成整本书（此方式生成的目录无法更新）</p>
  </div>
  <p style="margin:0px;background-color: rgb(255, 153, 0);color:#ffffff;width:100%;text-align: center;">长按弹窗任意空白处关闭弹窗</p>
</div>`;

    let time, isLong;
    function onBtnStart(evt) {
      evt.preventDefault();
      time = setTimeout(() => {
        isLong = true;
        autoBtn.style.display = "block";
      }, 600);
    };
    function onBtnEnd(evt) {
      evt.preventDefault();
      if (isLong) {
        isLong = undefined;
      } else {
        clearTimeout(time);
        time = undefined;
        window.open(`legado://import/addToBookshelf?src=${encodeURIComponent(window.location.href)}`, '_blank');
      }
    }
    btn.addEventListener('touchstart', onBtnStart);
    btn.addEventListener('touchend', onBtnEnd);
    btn.addEventListener('touchcancel', onBtnEnd);
    btn.addEventListener('mousedown', onBtnStart);
    btn.addEventListener('mouseup', onBtnEnd);
    document.body.appendChild(btn);

    let time2, isLong2;
    function onBtnStart2(evt) {
      time2 = setTimeout(() => {
        isLong2 = true;
        autoBtn.style.display = "none";
      }, 600);
    };
    function onBtnEnd2(evt) {
      if (isLong2) {
        evt.preventDefault();
        isLong2 = undefined;
      } else {
        clearTimeout(time2);
        time2 = undefined;
      }
    }

    autoBtn.addEventListener('touchstart', onBtnStart2);
    autoBtn.addEventListener('touchend', onBtnEnd2);
    autoBtn.addEventListener('touchcancel', onBtnEnd2);
    autoBtn.addEventListener('mousedown', onBtnStart2);
    autoBtn.addEventListener('mouseup', onBtnEnd2);
    document.body.appendChild(autoBtn);
  }

  let time, num = 0;
  const herfs = new Map();
  function onTouchStart(evt) {
    for (const el of evt.path) {
      if (el.tagName === "A") {
        if (el.href !== 'javascript:void(0)') {
          const inner = el.href;
          herfs.set(el, inner);
          el.setAttribute('href', 'javascript:void(0)');
          time = setTimeout(() => {
            window.open(`legado://import/addToBookshelf?src=${encodeURIComponent(inner)}`, '_blank')
          }, 600);
        }
        break;
      }
    }
  };
  function onTouchEnd(evt) {
    if (time) {
      clearTimeout(time);
      time = undefined;
    }
    for (const el of evt.path) {
      if (el.tagName === "A") {
        if (el.href === 'javascript:void(0)') {
          const herf = herfs.get(el);
          if (herf) {
            el.setAttribute('href', herf)
          }
        }
        break;
      }
    }
  }

  function addListener(els) {
    Array.from(els, (item) => {
      if (item.getAttribute('add-yuedu-bookshelf') !== 'add-yuedu-bookshelf' && item.innerText && item.href && !/^#|^javascript:/.test(item.href)) {
        item.setAttribute('add-yuedu-bookshelf', 'add-yuedu-bookshelf');
        item.addEventListener('touchstart', onTouchStart);
        item.addEventListener('touchend', onTouchEnd);
        item.addEventListener('touchcancel', onTouchEnd);
        item.addEventListener('mousedown', onTouchStart);
        item.addEventListener('mouseup', onTouchEnd);
      }
    });
    num = els.length;
  }

  function reAdd() {
    const els = document.getElementsByTagName('a');
    if (els.length > num) addListener(els);
  }

  reAdd();

  const observer = new ResizeObserver(reAdd);
  observer.observe(document.body);

