const host = document.location.host;
const path = document.location.pathname;
const params = new URLSearchParams(window.location.search);
const currentUrl = window.location.href;
const url = new URL(currentUrl);

// 域名重定向
if (host === 'changdunovel.com' && path.endsWith('share-v2.html')) {
  const bookid = params.get('book_id');
  if (bookid) {
    document.location.href = `https://fanqienovel.com/page/${bookid}`;
  }
}

const adBlockSystem = setInterval(() => {
  const shouldSkip = (el) => {
    return el.closest('.horizontal-slip-modal-card') || 
           el.closest('.topic-comment-item.new-card-style');
  };
 document.querySelectorAll('.page-reader-btn.download, .download-btn, .app-download-popup, .download-app')
    .forEach(el => {
      if (!shouldSkip(el) && el.textContent.match(/下载|APP|安装/)) {
        el.remove();
      }
    });
  document.querySelectorAll('.mask, .popup-layer, .modal-backdrop, .popup-container')
    .forEach(el => {
      if (!shouldSkip(el)) {
        el.remove();
      }
    });
  document.querySelectorAll('.float-ad, .bottom-ad, .fixed-ad')
    .forEach(ad => {
      if (!shouldSkip(ad)) {
        ad.remove();
      }
    });
}, 500);

const fixBookClick = setInterval(() => {
  const shouldSkip = (el) => {
    return el.closest('.horizontal-slip-modal-card') || 
           el.closest('.topic-comment-item.new-card-style');
  };

  document.querySelectorAll('.horizontal-slip-books-item').forEach(item => {
    if (shouldSkip(item)) return;

    item.style.pointerEvents = 'auto';
    item.style.cursor = 'pointer';

    if (item.id && !item.hasAttribute('data-click-fixed')) {
      item.setAttribute('data-click-fixed', 'true');
      item.addEventListener('click', (e) => {
        e.preventDefault();
        window.location.href = `https://fanqienovel.com/page/${item.id}`;
      });
    }
  });

  document.querySelectorAll('div.books-fold-card-book-item.marrow-book-item').forEach(item => {
    if (shouldSkip(item)) return;

    item.style.pointerEvents = 'auto';
    item.style.cursor = 'pointer';

    const bookId = item.getAttribute('data-book-id') || item.id;
    if (bookId && !item.hasAttribute('data-click-fixed')) {
      item.setAttribute('data-click-fixed', 'true');
      item.addEventListener('click', (e) => {
        e.preventDefault();
        window.location.href = `https://fanqienovel.com/page/${bookId}`;
      });
    }
  });
}, 1000);

const paginationSystem = setInterval(() => {
  const shareBottom = document.querySelector(".share-bottom-button");
  const shouldSkip = (el) => {
    return el && (el.closest('.horizontal-slip-modal-card') || 
                 el.closest('.topic-comment-item.new-card-style'));
  };

  if (shareBottom && !shouldSkip(shareBottom)) {
    shareBottom.remove();
    const shareApp = document.querySelector(".share-end-href-app");
    const offset = Number(url.searchParams.get('offset') || "0");

    if (shareApp && !shouldSkip(shareApp)) {
      shareApp.parentNode.innerHTML = `
        <div class="share-end-href-app" style="display:flex;justify-content:center;gap:20px;padding:12px 0;">
          ${offset === 0 ? "" : `<a style="padding:8px 16px;border-radius:4px;background:#f0f0f0;color:#333;text-decoration:none;" onclick="changePage(${offset-1}, url)">上一页</a>`}
          <span style="padding:8px 16px;color:#666;">${offset+1}</span>
          <a style="padding:8px 16px;border-radius:4px;background:#f0f0f0;color:#333;text-decoration:none;" onclick="changePage(${offset+1}, url)">下一页</a>
        </div>
      `;
    }
  }
}, 1000);

function changePage(value, url) {
  url.searchParams.set('offset', value);
  location.replace(url);
}

const style = document.createElement('style');
style.textContent = `
  .page-reader-btn.download:not(.horizontal-slip-modal-card *):not(.topic-comment-item.new-card-style *),
  .download-btn:not(.horizontal-slip-modal-card *):not(.topic-comment-item.new-card-style *),
  .app-download-popup:not(.horizontal-slip-modal-card *):not(.topic-comment-item.new-card-style *) {
    display: none !important;
    visibility: hidden !important;
    pointer-events: none !important;
    opacity: 0 !important;
  }

  .topic-comment-item:not(.new-card-style):not(.horizontal-slip-modal-card *) {
    pointer-events: none !important;
    user-select: none !important;
  }

  .horizontal-slip-books-item:not(.horizontal-slip-modal-card *):not(.topic-comment-item.new-card-style *),
  div.books-fold-card-book-item.marrow-book-item:not(.horizontal-slip-modal-card *):not(.topic-comment-item.new-card-style *) {
    pointer-events: auto !important;
    cursor: pointer !important;
  }
`;
document.head.appendChild(style);

setTimeout(() => {
  clearInterval(adBlockSystem);
  clearInterval(fixBookClick);
}, 30000);

