if(!/apk\/detail/.test(baseUrl)){
       let html = `
<link rel="stylesheet" href="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.2/viewer.min.css">
<script src="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.2/viewer.min.js"></script>`;
       html += `<style>
      .p { margin-top: 12px; }
      .box-vertical { display: flex; flex-direction: column; word-wrap: break-word; word-break: break-all; }
      #box{height:320px}
      #box,.box-horizontal{ display: flex; flex-direction: row; overflow: scroll; }
      .font-small { font-size: 13px; }
      .font-medium { font-size: 15px; }
      .color-grey { color: grey; }
      .avatar { width: 30px; height: 30px; margin-right: 6px;border-radius:50% }
      .image { flex: 0 0 auto;  margin-left: 1px;min-width:160px }
      .hr { width: 100%; height: 1px; border: none; border-top: thin solid #eee; }
      .padding { padding-left: 40px; }
      .line-vertical { margin-right: 6px; border-left: thin solid #eee; }
    </style>`
    html += `<div class='box-vertical'>`
    let id = "{{$.data.id}}";
    let item = JSON.parse(result).data;
    html += createReply(item)
    let curPage  = 1;
    let item_reply = []
    let totalnum = 0;
    let lastItem =""
    let resp2 = JSON.parse(java.ajax("https://api.coolapk.com/v6/feed/replyList?id={{$.data.id}}&page=1&lastItem="));
    let item2 = resp2.data
    item_reply = item_reply.concat(item2)
    let replynum5 = item.replynum;
    replynum5 > 100?replynum5=100:replynum5
    while (item2.length && (totalnum = totalnum + item2.length) < replynum5) {
      curPage = curPage + 1
      lastItem = item2[item2.length - 1].id
      resp2 = resp2 = JSON.parse(java.ajax(`https://api.coolapk.com/v6/feed/replyList?id=${id}&page=${curPage}&lastItem=${lastItem}`))
      item2 = resp2.data
      item_reply = item_reply.concat(item2)
    }

    let grouped_item_reply = {}
    item_reply.map(item => {
      if (item.rrid) {
        grouped_item_reply[item.rrid] = grouped_item_reply[item.rrid] || []
        grouped_item_reply[item.rrid].push(item)
      } else {
        grouped_item_reply[item.id] = grouped_item_reply[item.id] || []
        grouped_item_reply[item.id].push(item)
      }
    })
    for (key in grouped_item_reply) {
      grouped_item_reply[key].sort((e1, e2) => {
        return e1.dateline - e2.dateline
      }).map(item => html += createReply(item, true, !!item.rrid))
    }

    html += `</div>`
    html += `<script>
    box = document.getElementById("box");

    window.onload = () => {new Viewer(box,{title:false,interval:3000})
    }
    </script>`
    result = html
 }else{
 	html = `<p><h2><img style="width:30px;heigth:30px" src="{{$.data.logo}}">{{$.data.title}} <button onclick="c()">点击按钮复制分类Url规则粘贴查看评论</button></h2> <a href="https://www.coolapk.com/apk/{{$.data.apkname}}">软件地址</a>  </p>
 	<p>☼版本号:{{$.data.version}}</p>
<p>☉最近更新：{{$.data.changehistory##\n.*}}</p>
<p>☪︎{{$.data.apksize}}/{{$.data.downCount}}下载/{{$.data.followCount}}人关注/{{$.data.commentCount}}人评论/{{$.data.language}}</p>
<br>&lrm;<br>
<p>📖新版特性📖</p>
{{$.data.changelog}}
<br>&lrm;<br>
<p>📖应用介绍📖</p>
{{$.data.introduce}}`

   	html += `<textarea id="textarea" readonly>{{$.data.title}}▪回复::https://api.coolapk.com/v6/page/dataList?url=#/feed/apkCommentList?id={{$.data.id}}&sort=&title=最近回复&page={\{page}}&lastItem=\n`

html += "{{$.data.title}}▪发布::https://api.coolapk.com/v6/page/dataList?url=#/feed/apkCommentList?id=256030&sort=dateline_desc&title=最近发布&page={\{page}}&lastItem=\n"

html += "{{$.data.title}}▪热度::https://api.coolapk.com/v6/page/dataList?url=#/feed/apkCommentList?id={{$.data.id}}&sort=popular&title=热度排序&page={\{page}}&lastItem=\n</textarea>"
html += `<script>
function c() {
    var text = document.getElementById('textarea');
    // 选中文本
    text.select()
    // 复制
    document.execCommand('copy');
   alert("复制成功，请编辑源粘贴至分类url处")
}
</script>`
