for (var i = 0; i < lines.length; i++) {
    var line = lines[i].trim();
    if (line === '') continue;

    // 如果以 ◎ 开头，则是分组名
    if (line.startsWith('◎')) {
        // 如果当前分组名和内容已存在，保存上一个分组
        if (groupName && content) {
            var encodedContent = java.base64Encode(content); // 使用 java.base64Encode
            result.push(`${groupName}::data:;base64,${encodedContent},{"type":""}`);
        }
        groupName = line.slice(1, -1).trim(); // 去掉 ◎，设置新的分组名
        content = ''; // 重置内容
    } else if (line.startsWith('【')) {
        // 如果以 【 开头，则是内容的一部分
        content += line + '\n';
    }
}

// 保存最后一个分组
if (groupName && content) {
    var encodedContent = java.base64Encode(content); // 使用 java.base64Encode
    result.push(`${groupName}::data:;base64,${encodedContent},{"type":""}`);
}

