//网站检错
function resolveLink(link, timeout = 5000) {
    try {
        // 设置超时
        var url = new java.net.URL(link);
        var connection = url.openConnection();
        connection.setConnectTimeout(timeout);
        connection.setReadTimeout(timeout);
        connection.connect();

        // 从链接中提取主机名
        var hostname = url.getHost();

        // 解析主机名
        var address = java.net.InetAddress.getByName(hostname);
        var ipAddress = address.getHostAddress();

        return "正常";
    } catch (e) {
        if (e.javaException instanceof java.net.UnknownHostException) {
            return "无法解析链接中的主机名: " + link;
        } else if (e.javaException instanceof java.net.MalformedURLException) {
            return "链接格式错误: " + link;
        } else if (e.javaException instanceof java.net.SocketTimeoutException) {
            return "连接超时: " + link;
        } else if (e.javaException instanceof java.io.IOException) {
            return "IO异常: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.net.ConnectException) {
            return "无法连接到服务器: " + link;
        } else if (e.javaException instanceof java.net.NoRouteToHostException) {
            return "没有到主机的路由: " + link;
        } else if (e.javaException instanceof java.net.ProtocolException) {
            return "协议错误: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.net.SocketException) {
            return "套接字错误: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.net.BindException) {
            return "绑定错误: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.net.PortUnreachableException) {
            return "端口不可达: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.net.URISyntaxException) {
            return "URI语法错误: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.security.GeneralSecurityException) {
            return "安全异常: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.lang.IllegalArgumentException) {
            return "非法参数: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.lang.NullPointerException) {
            return "空指针异常: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.lang.SecurityException) {
            return "安全异常: " + e.javaException.getMessage();
        } else if (e.javaException instanceof java.lang.UnsupportedOperationException) {
            return "不支持的操作: " + e.javaException.getMessage();
        } else {
            return "发生未知错误: " + e.javaException.getMessage();
        }
    }
}



//随机UA
function generateUserAgent(deviceOption = '') {
    // 用于随机生成UA
    // 输入的第一个参数大小不敏感

    // 定义常见的浏览器、操作系统和设备类型
    var browsers = [
        { name: "Chrome", highCompat: "80.0.3987." },
        { name: "Firefox", highCompat: "78.0." },
        { name: "Safari", highCompat: "13.1." },
        { name: "Edge", highCompat: "87.0.664." }
    ];
    var osPC = [
        "Windows NT 10.0; Win64; x64",
        "Macintosh; Intel Mac OS X 13_5",
        "Linux x86_64"
    ];
    var osMobile = [
        "Android 11; Mobile", // 高兼容的安卓版本
        "iPhone; CPU iPhone OS 13_1 like Mac OS X" // 高兼容的 iOS 版本
    ];
    var random = function(arr) {
        return arr[Math.floor(Math.random() * arr.length)];
    };

    // 根据第一个参数生成操作系统和设备类型
    var os, device;
    switch ((deviceOption || '').toLowerCase()) {
        case '电脑':
            os = random(osPC); // 随机选择 PC 操作系统
            device = ''; // PC 版不需要设备标识
            break;
        case '手机': // 手机版
            os = random(osMobile); // 随机选择高兼容的手机操作系统
            device = 'Mobile'; // 添加手机设备标识
            break;
        default: // 默认随机选择
            var allOS = osPC.concat(osMobile); // 所有操作系统
            os = random(allOS); // 随机选择操作系统
            device = os.indexOf('Android') !== -1 || os.indexOf('iPhone') !== -1 ? 'Mobile' : ''; // 如果是手机操作系统，添加设备标识
    }

    // 固定为高兼容版本
    var browser = random(browsers);
    var version = browser.highCompat + Math.floor(Math.random() * 100); // 高兼容版本

    // 生成最终的 UA
    return "Mozilla/5.0 (" + os + ") AppleWebKit/537.36 (KHTML, like Gecko) " + browser.name + "/" + version + " Safari/537.36" + (device ? " " + device : "");
}




 
