handleSetStation = () => {
    // 获取站点列表和站点序号
    var stationList = result;
    var stationIndex = stationList.站点序号;
    // 获取并检查站点列表是否初始化
    var resultList = java.get('resultList');
    if (resultList == "") {
        return java.longToast("\n❗️站点列表未初始化，请先初始化站点！！");
    }
    // 将站点列表拆分为数组
    resultList = resultList.split(',');
    // 检查站点序号是否为空
    if (stationIndex == "") {
        return java.longToast("\n❗️输入为空，请输入有效序号");
    }
    // 转换中文数字和带圈数字为阿拉伯数字
    var chineseNumbers = { 
        "一": 1, "二": 2, "三": 3, "四": 4, "五": 5, "六": 6, "七": 7, "八": 8, "九": 9,
        "①": 1, "②": 2, "③": 3, "④": 4, "⑤": 5, "⑥": 6, "⑦": 7, "⑧": 8, "⑨": 9
    };
    stationIndex = chineseNumbers[stationIndex] || stationIndex;
    stationIndex = Number(stationIndex);
    // 在转换后的内容中匹配第一个数字串
    var firstNumberMatch = String(stationIndex).match(/\d+/);
    if (!firstNumberMatch) {
        return java.longToast("\n❗️输入无效，请输入有效序号");
    }
    stationIndex = Number(firstNumberMatch[0]);
    // 检查站点序号是否有效
    if (isNaN(stationIndex) || stationIndex <= 0 || stationIndex > resultList.length) {
        return java.longToast(stationIndex > resultList.length ? "\n❗️序号过大，请输入有效序号" : "\n❗️输入无效，请输入有效序号");
    }
    java.put('stationIndex',stationIndex)
 // 获取站点信息
    var stationInfo = resultList[stationIndex - 1];
    if (!stationInfo) {
        return java.longToast("\n❌获取站点信息失败，请检查站点列表");
    }
    // 提取站点URL和站点名称
    var stationUrl = (stationInfo.match(/:(.*)/) || [])[1] || "";
    if (!stationUrl) {
        return java.longToast("\n❌站点URL格式错误，请检查站点列表！！！");
    }
    var stationName = (stationInfo.match(/(.*):http/) || [])[1] || "";
    if (!stationName) {
        return java.longToast("\n❌站点名称格式错误，请检查站点列表！");
