injectJs:
let previousUrl = "";
let currentUrl = '';
let currentOffset = 0;
let totalPages = 1;
let isLoading = false;
let hasMore = true;
let hasPrevious = false;
let scrollDebounceTimer = null;

function encode(str) {
    str = str.replace(/https:.*?bq=/g,'');
    return btoa(encodeURI(str));
}

function decode(str) {
    str = "https://android.jjwxc.net/search/getSearchForKeyWords?offset=0&limit=20&bq=" + atob(str);
    return str;
}

// 书籍定位
function recordBookPosition(novelid,offset) {
    let previousUrl = sessionStorage.getItem('previousUrl');
    sessionStorage.setItem('bookPosition', JSON.stringify({
        url:previousUrl,
        novelid: novelid,
        offset: offset    
    }));
}

function restoreBookPosition() {
    const bookPosition = sessionStorage.getItem('bookPosition');
    if (!bookPosition) return;

    let { url, novelid, offset, timestamp } = JSON.parse(bookPosition);
    const pUrl = new URL(url);
    pUrl.searchParams.set('offset', offset);
    currentOffset = offset;
    fetchAndRender(pUrl.toString(), function(error) {
    if (error) {
        return;
    }

    const bookElement = document.querySelector(`.book[data-id="${novelid}"]`);
    if (bookElement) {
        updateCurrentPage()
        window.scrollTo({
            top: bookElement.offsetTop - 100,
            behavior: 'auto'
        });
    }
});

}

function fetchAndRender(url, callback) {
    showLoadingMessage();
    isLoading = true;
    sessionStorage.setItem('previousUrl',url);
    fetch(url)
        .then(response => response.json())
        .then(jsonData => {
            currentUrl = url;
            totalPages = jsonData.total_page;
            console.log(totalPages);
            currentOffset = parseInt(new URL(url).searchParams.get('offset')) || 0;
            hasMore = (Math.floor(currentOffset / 20) + 1) < jsonData.total_page;
            hasPrevious = currentOffset > 0; // 设置是否有上一页
            sessionStorage.setItem('previousUrl',url);
            generatePageContent(jsonData,currentOffset);
            addStyles();

            if (typeof callback === 'function') {
                callback(null, jsonData);
            }
        })
        .catch(error => {
            console.error('加载失败:', error);
            if (typeof callback === 'function') {
                callback(error);
            }
        })
        .finally(() => {
            isLoading = false;
            hideLoadingMessage();
            bookPosition = sessionStorage.getItem('bookPosition');        
            sessionStorage.removeItem('bookPosition');        
            if (!bookPosition) {
                window.scrollTo(0,0); 	
                return;
            }
        });
}

// 下拉加载
function initScrollListener() {    
    window.addEventListener('scroll', handleScroll, { passive: true });
}

function handleScroll() {    
    clearTimeout(scrollDebounceTimer);
    scrollDebounceTimer = setTimeout(() => {
        if (!isLoading) {
            updateCurrentPage();
        }        
        checkScrollBoundaries();
    }, 50);
}

function checkScrollBoundaries() {
    if (isLoading) return;

    const scrollPosition = window.scrollY;
    const documentHeight = document.body.offsetHeight;
    const windowHeight = window.innerHeight;

    if (scrollPosition + windowHeight >= documentHeight - 100 && hasMore) {
        loadNextPage();
    }

    if (scrollPosition <= 100 && hasPrevious) {
        loadPreviousPage();
    }
}

function loadPreviousPage() {
    if (isLoading) return;

    isLoading = true;
    document.getElementById('loading').style.display = 'block';

    const newOffset = Math.max(0, currentOffset - 20);
    const url = new URL(currentUrl);
    url.searchParams.set('offset', newOffset);

    // 保存当前滚动位置
    const scrollPositionBefore = window.scrollY;

    fetch(url.toString())
        .then(response => response.json())
        .then(jsonData => {
                requestAnimationFrame(() => {
                prependBookElements(jsonData, newOffset);                                
                currentOffset = newOffset;
                updateCurrentPage();
                hasMore = (Math.floor(newOffset / 20) + 1) < jsonData.total_page;
                hasPrevious = newOffset > 0;           
                const newBooks = document.querySelectorAll('.book');
                if (newBooks.length > 0) {              
                    const targetBook = newBooks[jsonData.items.length - 1];
                    if (targetBook) {
                        const targetPosition = targetBook.offsetTop +100; 
                        window.scrollTo({
                            top: targetPosition,
                            behavior: 'auto'
                        });
                    }
                }
            });
        })
        .catch(error => {
            console.error('加载上一页失败:', error);
        })
        .finally(() => {
            isLoading = false;
            document.getElementById('loading').style.display = 'none';
        });
}

// 优化后的加载下一页函数
function loadNextPage() {
    if (isLoading) return;

    isLoading = true;
    document.getElementById('loading').style.display = 'block';

    const newOffset = currentOffset + 20;
    console.log(newOffset);

    const url = new URL(currentUrl);
    url.searchParams.set('offset', newOffset);

    fetch(url.toString())
        .then(response => response.json())
        .then(jsonData => {
            requestAnimationFrame(() => {
                currentOffset = newOffset;
                appendBookElements(jsonData, newOffset);
                updateCurrentPage();
                hasMore = (Math.floor(newOffset / 20) + 1) < jsonData.total_page;
            });
        })
        .catch(error => {
            console.error('加载下一页失败:', error);
        })
        .finally(() => {
            isLoading = false;
            document.getElementById('loading').style.display = 'none';
        });
}

function createBookElement(item,offset){
let bookDiv = document.createElement('div');
        bookDiv.className = 'book';
        bookDiv.setAttribute('data-id', item.novelid);
        bookDiv.setAttribute('data-offset', offset);

        bookDiv.innerHTML = `
            <div class="bookdes">
                <div class="pic"><img src="${item.cover}" referrerpolicy="no-referrer"></div>
                <div class="details">
                    <p class="bookname">
                        <span class="name"><a href="https://m.jjwxc.net/book2/${item.novelid}">${item.novelname}</a></span>
                        <br>
                        <span class="author">——${item.authorname}</span>
                    </p>
                    <p class="tag">⭐️ ${item.novelSizeformat}字•${item.novelstep === '2' ? '<span style="color:red">已完结</span>' : '<span style="color:blue">连载中</span>'}<br><br>📖 ${item.novelintroshort}<br><br>🔖 ${item.novelClass}<br><br>🏷 <span style="color:green">${item.tags}</span></p>
                </div>
            </div>
        `;

        const link = bookDiv.querySelector('a');
        link.addEventListener('click', (e) => {
            e.preventDefault();
            recordBookPosition(item.novelid, offset);
            window.location.href = link.href;
        });
return bookDiv
}

function prependBookElements(data, offset) {
    const bookContainer = document.getElementById('bookContainer');
    const fragment = document.createDocumentFragment();   
    data.items.forEach((item, index) => {       
        let bookDiv = createBookElement(item,offset)
        fragment.appendChild(bookDiv);
    });    
    bookContainer.insertBefore(fragment, bookContainer.firstChild);
}


// 书籍列表生成
function createBookElements(data,offset) {
    const bookContainer = document.getElementById('bookContainer');
    bookContainer.innerHTML = '';
    data.items.forEach((item, index) => {
        let bookDiv = createBookElement(item,offset)
        bookContainer.appendChild(bookDiv);     
    });
}

function appendBookElements(data,offset) {
    const bookContainer = document.getElementById('bookContainer');
    data.items.forEach((item, index) => {
        let bookDiv = createBookElement(item,offset)
        bookContainer.appendChild(bookDiv);
    });
}

// 页面操作
function updateUrlParam(key, value) {
    const url = new URL(currentUrl);
    url.searchParams.set(key, value);
    url.searchParams.set('offset', 0);
    currentOffset = 0;
    updateCurrentPage()
    fetchAndRender(url.toString());
}

function updateOffset(delta) {
    const url = new URL(currentUrl);
    const newOffset = Math.max(0, currentOffset + delta);
    currentOffset = newOffset;
    url.searchParams.set('offset', newOffset);
    fetchAndRender(url.toString());
}

function jumpToPage() {
    const pageInput = document.querySelector('.page-input');
    const page = parseInt(pageInput.value);
    if (isNaN(page) || page < 1 || page > totalPages) {
        alert(`请输入有效的页码(1-${totalPages})`);
        return;
    }

    currentOffset = (page - 1) * 20;
    const url = new URL(currentUrl);
    url.searchParams.set('offset', currentOffset);
    fetchAndRender(url.toString());
}

function updateCurrentPage() {
    const books = document.querySelectorAll('.book');
    if (books.length === 0) return;


    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const bookOffsetAttr = entry.target.getAttribute('data-offset');

const bookOffset = bookOffsetAttr !== null ? parseInt(bookOffsetAttr) : currentOffset;

                const currentPage = Math.floor(bookOffset / 20) + 1;


                const currentDisplay = document.querySelector('.current-page').textContent;
                if (currentDisplay !== currentPage.toString()) {
                    document.querySelector('.current-page').textContent = currentPage;
                    document.querySelector('.current').textContent = currentPage;
                    currentOffset = bookOffset;
                }
            }
        });
    }, {
        threshold: 0.5 
    });        
    books.forEach(book => {
        observer.observe(book);
    });

    return () => {
        observer.disconnect();
    };
}


// 辅助函数
function showLoadingMessage() {
    const loadingDiv = document.createElement('div');
    loadingDiv.id = 'loading-message';
    loadingDiv.style.position = 'fixed';
    loadingDiv.style.top = '20px';
    loadingDiv.style.left = '50%';
    loadingDiv.style.transform = 'translateX(-50%)';
    loadingDiv.style.backgroundColor = 'rgba(0, 0, 0, 0.8)';
    loadingDiv.style.color = 'white';
    loadingDiv.style.padding = '10px 20px';
    loadingDiv.style.borderRadius = '5px';
    loadingDiv.style.zIndex = '1000';
    loadingDiv.textContent = '正在加载，请稍候...';
    document.body.appendChild(loadingDiv);
}

function hideLoadingMessage() {
    const loadingDiv = document.getElementById('loading-message');
    if (loadingDiv) loadingDiv.remove();
}

function generateFinalUrl(formData) {
    const variables = {};
    for (let [key, value] of formData.entries()) {
        const variableName = key.replace(/\d+/g, '');
        if (!variables[variableName]) {
            variables[variableName] = [];
        }
        variables[variableName].push(value);
    }

    for (const key in variables) {
        variables[key] = variables[key].join(',');
    }

    return `https://android.jjwxc.net/search/getSearchForKeyWords?offset=0&limit=20&bq=${variables.bq || ''}&removetags=${variables.removebq || ''}&notlikecollectionTypes=${variables.notlikecollectiontypes || ''}&fw=${variables.fw || ''}&yc=${variables.yc || ''}&xx=${variables.xx || ''}&sd=${variables.sd || ''}&lx=${variables.lx || ''}&mainview=${variables.mainview || ''}&fbsj=${variables.fbsj || ''}&novelbefavoritedcount=${variables.novelbefavoritedcount || ''}&isfinish=${variables.isfinish || ''}&collectionTypes=${variables.collectiontypes || ''}&searchkeyWords=${variables.searchkeywords || ''}`;
}

// 页面生成
function generatePageContent(jsonData,offset) {
    document.documentElement.innerHTML = `
          <!-- 悬浮按钮组 -->
    <div class="float-buttons">
        <div class="button-group">
                    <!-- 页码控制面板 -->
            <div class="page-control">
                <div class="page-info">
                    <span class="current-page">1</span>
                    <div class="divider"></div>
                    <span class="total-pages">${jsonData.total_page}</span>
                </div>
                <div class="page-jump">
                    <input type="number" min="1" max="${jsonData.total_page}" value="1" class="page-input">
                    <button class="go-btn">GO</button>
                </div>
            </div>
            <!-- 功能按钮（从下到上顺序） -->
            <button class="func-btn home" title="返回首页">⌂</button>
            <button class="func-btn bottom" title="页面底部">↓</button>
            <button class="func-btn top" title="页面顶部">↑</button>


        </div>

        <!-- 主按钮 - 深绿色圆形 -->
        <button class="main-btn" title="展开功能">
            <span class="current">1</span>
            <div class="divider"></div>
            <span class="total">${jsonData.total_page}</span>
        </button>
    </div>

            <div class="center" style="text-align: center;">
                <select name="sortType" id="orderstr" style="width:25%">
                    <option value="2">积分</option>
                    <option value="1">最近更新</option>
                    <option value="3">最新发表</option>
                    <option value="5">字数</option>
                    <option value="4">收藏数</option>
                    <option value="10">完结高分</option>
                </select>
                <span style="margin: 0 2px;">|</span>
                <select name="isfinish" id="isfinish" style="width:25%">
                    <option value="0">无限制</option>
                    <option value="1">连载</option>
                    <option value="2">完结</option>
                </select>
                <span style="margin: 0 2px;">|</span>
                <button id="biaoqian" style="width:25%">
                    标签管理                  
                </button>
            </div>
        <div style="height: 60px;"></div>
        <br>
        <div id="bookContainer" class="book-container"></div>
        <div id="loading" style="text-align: center; padding: 10px; display: none;">
            正在加载更多...
        </div>
    `;

    const sortTypeSelect = document.getElementById("orderstr");
    const isFinishSelect = document.getElementById("isfinish");
    const urlParams = new URLSearchParams(currentUrl.split('?')[1]);

    const mainBtn = document.querySelector('.main-btn');
    const floatButtons = document.querySelector('.float-buttons');
    const totalPagesEl = document.querySelector('.total-pages');
    const pageInput = document.querySelector('.page-input');
    const pageSections = document.querySelectorAll('.page-section');
    const homeBtn = document.querySelector('.func-btn.home');
    const topBtn = document.querySelector('.func-btn.top');
    const bottomBtn = document.querySelector('.func-btn.bottom');

            // 设置总页数
        totalPages = jsonData.total_page; 
        totalPagesEl.textContent = totalPages;
        pageInput.max = totalPages;

        // 检测是否为长页码
        if (totalPages >= 10000) {
            floatButtons.classList.add('long-page');
        }


        mainBtn.addEventListener('click', function(e) {
            e.stopPropagation();
            floatButtons.classList.toggle('expanded');
            mainBtn.style.display = 'none';

        });

        // 点击页面其他区域收起按钮组
        document.addEventListener('click', function() {
            floatButtons.classList.remove('expanded');
            mainBtn.style.display = 'flex';
        });


        document.querySelector('.button-group').addEventListener('click', function(e) {
            e.stopPropagation();
        });


        document.querySelector('.go-btn').addEventListener('click', jumpToPage);
        document.querySelector('#biaoqian').addEventListener('click',function() {    
            toggleTag(sessionStorage.getItem('previousUrl'))
        });


        homeBtn.addEventListener('click', function() {
            sessionStorage.setItem('previousUrl', "");
            window.location.reload();
        });

        topBtn.addEventListener('click', function() {
            window.scrollTo({top: 0, behavior: 'smooth'});
            floatButtons.classList.remove('expanded');
            mainBtn.style.display = 'flex';
        });

        bottomBtn.addEventListener('click', function() {
            window.scrollTo({top: document.body.scrollHeight-100, behavior: 'smooth'});
            floatButtons.classList.remove('expanded');
            mainBtn.style.display = 'flex';
        });

    sortTypeSelect.value = urlParams.get('sortType') || '2';
    isFinishSelect.value = urlParams.get('isfinish') || '0';
    sortTypeSelect.onchange = () => updateUrlParam('sortType', sortTypeSelect.value);
    isFinishSelect.onchange = () => updateUrlParam('isfinish', isFinishSelect.value);


    initScrollListener();
    createBookElements(jsonData,offset);
    updateCurrentPage();

    const checkScrollable = () => {
        if (document.body.scrollHeight <= window.innerHeight) {

            if (hasMore) {
                loadNextPage();
            } else if (hasPrevious) {

                loadPreviousPage();
            }
        }
    };


    checkScrollable();
}

window.addEventListener('beforeunload', () => {
    window.removeEventListener('scroll', handleScroll);
    window.removeEventListener('resize', checkScrollable);
});


function createTag(url){    
        const form = document.getElementsByTagName('form')[1];
        const formData = new FormData(form);

        const finalUrl = url==undefined?generateFinalUrl(formData):url;                                           
        const dialog = createModalDialog();               
        const urlTextarea = document.createElement('textarea');
        urlTextarea.value = `标签名@${encode(finalUrl)}#`;
        urlTextarea.style.width = '90%';
        urlTextarea.style.height = '100px';
        urlTextarea.style.padding = '10px';
        urlTextarea.style.margin = '10px';
        urlTextarea.style.outline ='none';
        urlTextarea.style.border ='1px solid #ddd';
        urlTextarea.style.resize = 'none';

        dialog.dialog.prepend(urlTextarea);

        const promptText = document.createElement('p');
        promptText.textContent = '修改标签名，点击保存';
        promptText.style.margin = '10px 0';
        promptText.style.fontSize = '14px';
        promptText.style.color = '#333';
        dialog.dialog.prepend(promptText);

        const copyButton = document.createElement('button');
        copyButton.textContent = '保存';
        copyButton.style.margin = '10px';
        copyButton.style.padding = '10px 20px';
        copyButton.style.backgroundColor = '#28a745';
        copyButton.style.color = 'white';
        copyButton.style.border = 'none';
        copyButton.style.borderRadius = '5px';
        copyButton.style.cursor = 'pointer';
        copyButton.addEventListener('click', () => {
            let tag = urlTextarea.value;
            let tags = localStorage.getItem("setTag");
            localStorage.setItem("setTag",tags+tag);
            let saveTag = localStorage.getItem("setTag");            
            alert('已保存标签：'+tag);
            document.body.removeChild(dialog.overlay);
        });
        dialog.dialog.appendChild(copyButton);          
}


//标签管理弹窗
function createModalDialog(options = {}) {
    //遮罩层
    const overlay = document.createElement('div');
    overlay.style.position = 'fixed';
    overlay.style.top = '0';
    overlay.style.left = '0';
    overlay.style.width = '100%';
    overlay.style.height = '100%';
    overlay.style.backgroundColor = 'rgba(0, 0, 0, 0.5)';
    overlay.style.zIndex = '1001';
    overlay.style.display = 'flex';
    overlay.style.justifyContent = 'center';
    overlay.style.alignItems = 'center';

    //弹窗
    const dialog = document.createElement('div');
dialog.style.cssText = `
    background-color: white;
    padding: 10px;
    border-radius: 10px;
    width: 80%;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
    text-align: center;
    max-height: 60vh;
`;


    if (options.title) {
        const title = document.createElement('h2');
        title.textContent = options.title;
        title.style.marginTop = '0';
        dialog.appendChild(title);
    }

    if (options.content && Array.isArray(options.content)) {
        options.content.forEach(element => {
            dialog.appendChild(element);
        });
    }

    // 创建关闭按钮
    const closeButton = document.createElement('button');
    closeButton.textContent = '关闭';
    closeButton.style.margin = '10px';
    closeButton.style.padding = '10px 20px';
    closeButton.style.backgroundColor = '#dc3545';
    closeButton.style.color = 'white';
    closeButton.style.border = 'none';
    closeButton.style.borderRadius = '5px';
    closeButton.style.cursor = 'pointer';
    closeButton.addEventListener('click', () => {
        document.body.removeChild(overlay);
        if (typeof options.onClose === 'function') {
            options.onClose(); 
        }
    });
    dialog.appendChild(closeButton);

    overlay.appendChild(dialog);
    document.body.appendChild(overlay);

     return {
        dialog: dialog, 
        overlay: overlay
    };
}

function createTagButton(type){   
    const tagsWrapper = document.createElement('div');
    tagsWrapper.style.cssText = `
        margin: 5px;
        padding:5px;
        max-height:50vh;
        overflow-y: auto;
        display: flex;
        flex-wrap: wrap;
        align-items: flex-start;
    `;
    let url = localStorage.getItem('setTag');
    const urls = url.split('#');
    const buttonData = urls.filter(part => part.trim() !== '');        
    buttonData.forEach((data, index) => {
        const [label, url] = data.split('@');
        const button = document.createElement('button');
        button.textContent = decodeURIComponent(label) || `标签${index + 1}`;
        button.style.cssText = `
            padding: 5px 5px;
            border-radius: 5px;
            white-space: wrap;
            flex-shrink: 0;
            background-color: #2196F3;
            color: white;
            border: none;
            cursor: pointer;
            transition: all 0.3s;
            margin-right: 10px;
            margin-bottom: 10px;
            min-width: 60px;
            max-width: 100%;
        `;
        if(type!==0){       
            button.addEventListener('click', () => fetchAndRender(decode(url)));
        }else if(type===0){
        button.addEventListener('click', () => {
        try{
        document.querySelectorAll("input").forEach(input => {

  input.checked = false;

  // 2. 找到相邻的兄弟 span 并变红
  const span = input.nextElementSibling; // 获取紧邻的下一个兄弟元素
  if (span && span.tagName === 'SPAN') {
    span.style.color = 'black';
  }
});

        document.querySelectorAll('.del_choosed_tag').forEach(button => {
    button.click();
});              
        let eurl = decodeURIComponent(decodeURIComponent(decode(url)));
        eurl = eurl.toLowerCase().replace(/http.*?\?|offset=\d+&|limit=\d+&|isfinish=\d+&/g,'').split('&'); 
        for(let i=0;i<eurl.length;i++){
            let params = eurl[i].split("=");
            let names = params[0];                 
            let values = params[1].split(",");                   
            for(let j=0;j<values.length;j++){
                let name,input;
      if(names==="bq"||names==="removetags"){
      if(values[j]){
              addBq(params[1],values[j],names)
             }
              }else if(/\d+/.test(values[j])){
                name = names+values[j];       
                input = document.querySelector(`input[name="${name}"]`);
                if(input){
                  if (input.nextElementSibling && input.nextElementSibling.tagName === 'SPAN') {
    input.nextElementSibling.style.color = 'red';
  }
  input.checked=true
        }        

              }else{
              input = document.querySelector(`input[name="${names}"][value="${values[j]}"]`);
              input?input.checked=true:null
              }            	       
            }                                         
        }

        }catch(e){alert(e)}              
        });               
     }
        tagsWrapper.appendChild(button);        
    });
    return tagsWrapper
}

