let id = baseUrl.match(/(\d+)/)[1];
let api = `https://m.weibo.cn/statuses/extend?id=${id}`;
java.setContent(String(java.ajax(api)));
let b = baseUrl;
var html = result;
retweeted_status = String(java.getString("$.data.retweeted_status")) ? "<hr class='divider'>" + java.getString("$.data.retweeted_status.longTextContent") : "";
result = java.getString("$.data.longTextContent")+ retweeted_status;
allImages = [];
if(html.indexOf('"media_info"') > -1) {
    urls = JSON.parse(html.match(/"urls": ({[\s\S]+?})/)[1]);
    media_info = JSON.parse(html.match(/"media_info": ({[\s\S]+?})/)[1]);

    if(urls.mp4_720p_mp4) { a = urls.mp4_720p_mp4; }
    else if(urls.mp4_hd_mp4) { a = urls.mp4_hd_mp4; }
    else if(media_info.stream_url_hd) { a = media_info.stream_url_hd; }
    else { a = media_info.stream_url; }

    result = result.replace(/data-url="([^"]+)"\s*href="([^"]+)"/, `data-url="$1" href="${a}"`);
    dimg = html.match(/"page_pic":[\s\S]+?"url":\s*"([^"]+)"/)[1];

    result += `<div class="media-container"><video src="${a}" controls poster="${dimg}"></video></div>`;
} else if(html.match(/"size":\s*"large",\s*"url":\s*"[^"]+"/g)) {
    a = html.match(/"size":\s*"[^"]+",\s*"url":\s*"[^"]+"/g);
    var cimg = [];
    for (i = 0; i < a.length; i++) {
        cimg.push(a[i].match(/url":\s*"([^"]+)"/)[1]);
    };
    allImages = cimg.slice();

    var galleryItems = cimg.map((img, i) => `
        <div class="gallery-item" onclick="showFullImage('${img.replace(/'/g, "\\'")}', ${i})">
            <img src="${img}?thumbnail=1" loading="lazy">
            <div class="zoom-icon">🔍</div>
        </div>`).join('');

    result += `<div class="image-gallery">${galleryItems}</div>`;
}

// 处理评论
if(JSON.parse(java.ajax(`https://m.weibo.cn/comments/hotflow?id=${id}&mid=${id}&max_id_type=0`)).ok == 1) {
    list = JSON.parse(java.ajax(`https://m.weibo.cn/comments/hotflow?id=${id}&mid=${id}&max_id_type=0`)).data.data;
    result = `<a href="${b}" class="original-link">原文地址</a><div class="main-content">${result}</div>`;

    var comments = list.map(item => {
        var details = "";
        if(item.pic != undefined) {
            pic = item.pic.large.url;
            allImages.push(pic);
            const imgIndex = allImages.length - 1;
            // 处理评论中的图片部分
details = `
<details class="comment-media">
    <summary>
        <span class="media-preview">📷 查看图片</span>
    </summary>
    <div onclick="showFullImage('${pic.replace(/'/g, "\\'")}', ${imgIndex})">
        <img src="${pic}?thumbnail=1" alt="评论图片">
    </div>
</details>`;

        }

        return `
        <div class="comment-item">
            <div class="comment-user">
                <img class="user-avatar" src="${item.user.profile_image_url}">
                <span class="user-name">${item.user.screen_name}</span>
            </div>
            <div class="comment-text">${item.text}</div>
            ${details}
            <div class="comment-time">${item.created_at.replace(/\+0800/, "·")}</div>
        </div>`;
    }).join('');

    result += `<div class="comments-section"><h3 class="comments-title">评  论</h3>${comments}</div>`;
} else {
    result = `<a href="${b}" class="original-link">原文地址</a><div class="main-content">${result}</div>`;
}

