let previousUrl = "";
let currentUrl = '';
let bqUrl = "";
let currentOffset = 0;
let totalPages = 1;
let isLoading = false;
let hasMore = true;
let hasPrevious = false;
let scrollDebounceTimer = null;

// æ ‡ç­¾ç¼–è§£ç 
function encode(str) {
    str = str.replace(/https:.*?bq=/g,'');
    return btoa(encodeURI(str));
}

function decode(str) {
    str = "https://android.jjwxc.net/search/getSearchForKeyWords?offset=0&limit=20&bq=" + atob(str);
    return str;
}

// ä¹¦ç±å®šä½
function recordBookPosition(novelid,offset) {
    let previousUrl = sessionStorage.getItem('previousUrl');
    sessionStorage.setItem('bookPosition', JSON.stringify({
        url:previousUrl,
        novelid: novelid,
        offset: offset    
    }));
}

function restoreBookPosition() {
    const bookPosition = sessionStorage.getItem('bookPosition');
    if (!bookPosition) return;

    let { url, novelid, offset, timestamp } = JSON.parse(bookPosition);
    const pUrl = new URL(url);
    pUrl.searchParams.set('offset', offset);
    currentOffset = offset;
    fetchAndRender(pUrl.toString(), function(error) {
    if (error) {
        console.error('åŠ è½½å¤±è´¥ï¼Œæ— æ³•æ»šåŠ¨:', error);
        return;
    }

    const bookElement = document.querySelector(`.book[data-id="${novelid}"]`);
    if (bookElement) {
        updateCurrentPage()
        window.scrollTo({
            top: bookElement.offsetTop - 100,
            behavior: 'auto'
        });
    }
});

}

function fetchAndRender(url, callback) {
    showLoadingMessage();
    isLoading = true;
    sessionStorage.setItem('previousUrl',url);
    fetch(url)
        .then(response => response.json())
        .then(jsonData => {
            currentUrl = url;
            totalPages = jsonData.total_page;
            currentOffset = parseInt(new URL(url).searchParams.get('offset')) || 0;
            hasMore = (Math.floor(currentOffset / 20) + 1) < jsonData.total_page;
            hasPrevious = currentOffset > 0; // è®¾ç½®æ˜¯å¦æœ‰ä¸Šä¸€é¡µ
            sessionStorage.setItem('previousUrl',url);
            generatePageContent(jsonData,currentOffset);
            addStyles();

            if (typeof callback === 'function') {
                callback(null, jsonData);
            }
        })
        .catch(error => {
            console.error('åŠ è½½å¤±è´¥:', error);
            if (typeof callback === 'function') {
                callback(error);
            }
        })
        .finally(() => {
            isLoading = false;
            hideLoadingMessage();
            bookPosition = sessionStorage.getItem('bookPosition');        
            sessionStorage.removeItem('bookPosition');        
            if (!bookPosition) {
                window.scrollTo(0,0); 	
                return;
            }
        });
}

// ä¸‹æ‹‰åŠ è½½
function initScrollListener() {
    // ä½¿ç”¨è¢«åŠ¨äº‹ä»¶ç›‘å¬å™¨æé«˜æ»šåŠ¨æ€§èƒ½
    window.addEventListener('scroll', handleScroll, { passive: true });
}

function handleScroll() {
    // é˜²æŠ–å¤„ç†
    clearTimeout(scrollDebounceTimer);
    scrollDebounceTimer = setTimeout(() => {
        if (!isLoading) {
            updateCurrentPage();
        }

        // æ£€æŸ¥æ˜¯å¦åˆ°è¾¾è¾¹ç•Œ
        checkScrollBoundaries();
    }, 50);
}

function checkScrollBoundaries() {
    if (isLoading) return;

    const scrollPosition = window.scrollY;
    const documentHeight = document.body.offsetHeight;
    const windowHeight = window.innerHeight;

    // æ£€æŸ¥æ˜¯å¦åˆ°è¾¾åº•éƒ¨
    if (scrollPosition + windowHeight >= documentHeight - 100 && hasMore) {
        loadNextPage();
    }

    // æ£€æŸ¥æ˜¯å¦åˆ°è¾¾é¡¶éƒ¨
    if (scrollPosition <= 100 && hasPrevious) {
        loadPreviousPage();
    }
}

function loadPreviousPage() {
    if (isLoading) return;

    isLoading = true;
    document.getElementById('loading').style.display = 'block';

    const newOffset = Math.max(0, currentOffset - 20);
    const url = new URL(currentUrl);
    url.searchParams.set('offset', newOffset);

    // ä¿å­˜å½“å‰æ»šåŠ¨ä½ç½®
    const scrollPositionBefore = window.scrollY;

    fetch(url.toString())
        .then(response => response.json())
        .then(jsonData => {
            // ä½¿ç”¨requestAnimationFrameç¡®ä¿å¹³æ»‘åŠ è½½
            requestAnimationFrame(() => {
                // åŠ è½½æ–°å†…å®¹
                prependBookElements(jsonData, newOffset);

                // æ›´æ–°çŠ¶æ€
                currentOffset = newOffset;
                updateCurrentPage();
                hasMore = (Math.floor(newOffset / 20) + 1) < jsonData.total_page;
                hasPrevious = newOffset > 0;

                // è®¡ç®—å¹¶ä¿æŒæ»šåŠ¨ä½ç½®
                const newBooks = document.querySelectorAll('.book');
                if (newBooks.length > 0) {
                    // æ»šåŠ¨åˆ°æ–°åŠ è½½å†…å®¹çš„æœ€åä¸€æœ¬ï¼ˆå³ä¹‹å‰é‚£é¡µçš„ç¬¬ä¸€æœ¬ï¼‰
                    const targetBook = newBooks[jsonData.items.length - 1];
                    if (targetBook) {
                        const targetPosition = targetBook.offsetTop +100; // ç•™å‡ºä¸€äº›é¡¶éƒ¨ç©ºé—´

                        // ä½¿ç”¨å¹³æ»‘æ»šåŠ¨
                        window.scrollTo({
                            top: targetPosition,
                            behavior: 'auto'
                        });
                    }
                }
            });
        })
        .catch(error => {
            console.error('åŠ è½½ä¸Šä¸€é¡µå¤±è´¥:', error);
        })
        .finally(() => {
            isLoading = false;
            document.getElementById('loading').style.display = 'none';
        });
}

// ä¼˜åŒ–åçš„åŠ è½½ä¸‹ä¸€é¡µå‡½æ•°
function loadNextPage() {
    if (isLoading) return;

    isLoading = true;
    document.getElementById('loading').style.display = 'block';

    const newOffset = currentOffset + 20;
    const url = new URL(currentUrl);
    url.searchParams.set('offset', newOffset);

    fetch(url.toString())
        .then(response => response.json())
        .then(jsonData => {
            requestAnimationFrame(() => {
                currentOffset = newOffset;
                appendBookElements(jsonData, newOffset);
                updateCurrentPage();
                hasMore = (Math.floor(newOffset / 20) + 1) < jsonData.total_page;
            });
        })
        .catch(error => {
            console.error('åŠ è½½ä¸‹ä¸€é¡µå¤±è´¥:', error);
        })
        .finally(() => {
            isLoading = false;
            document.getElementById('loading').style.display = 'none';
        });
}

