function qiu(url){
   	var p=document.getElementById('Player')
   	var iframe=document.getElementById('KK')
   	$.ajax({
      url:url, 
      type: "GET", 
      success: function(data) {
      	 play=data.match(/player_aaaa=(.*})/)[1]
      	 $=JSON.parse(play)
      	 if($.from=="NBY"){
          play = 'https://play.silisili.top/player/ec.php?code=ttnb&if=1&url='+$.url
        }else{
          play = 'https://play.cilicili.one/player/?url='+$.url
        }
       iframe.src=play
       p.appendChild(iframe)
      },
      error: function(data){
      	alert('å¤±è´¥')
      }
    });
   }

