var ho='https://m.qidian.com'
var h='http://111.170.19.23:49998/'
function getComments(content, bid, cid) {
  // 主函数
  const { source, java, cache } = this;
  try {
    let separator = "\n";
    if (/<p>/.test(content)) {
      content = content.replace("<p>","").replace("\n", "");
      separator = "<p>";
    } else {
      content = content.replace(/\r/g, "").replace(/\n$/, "");
    }
    let comcont = content.split(separator);

    let comments = java.ajax(`https://m.qidian.com/majax/chapterReview/reviewSummary?bookId=${bid}&chapterId=${cid}&_csrfToken={{cookie.getKey("https://qidian.com","_csrfToken")}}`)
    JSON.parse(comments).data.list.forEach(x => {
      if (comcont[x.paragraphId - 1]) {
        cache.putMemory(`qdreader-${bid}-${cid}-${x.paragraphId}-text`, comcont[x.paragraphId - 1]);
        comcont[x.paragraphId - 1] += `<img src="${this.createSvg(x.textCount, bid, cid, x.paragraphId)}">`;
      }
    });
    return comcont.join("\n");
  } catch (e) {
    java.log(e);
    return content;
  }
}

function createSvg(number, bid, cid, para) {
  let displayText = number > 999 ? "999+" : number;
  let color = "red";
  let svg = `<svg width="1000" height="800" xmlns="http://www.w3.org/2000/svg">
    <rect x="25" y="25" width="950" height="750" rx="250" ry="250" fill="none" stroke="${color}" stroke-width="25"/>
    <text x="510" y="650" font-family="Arial, sans-serif" text-anchor="middle" dominant-baseline="middle" font-size="30" fill="#000000">刷新图片查看段评</text>
    <text x="510" y="565" font-family="Arial, sans-serif" text-anchor="middle" dominant-baseline="middle" font-size="425" fill="${color}">${displayText}</text>
</svg>
`;
  let encodedSvg = this.java.base64Encode(svg);
  let date = Date.now().toString();
  return `data:image/svg+xml;base64,${encodedSvg},{"style":"text","type":"qd","js":"showCmt(${bid}, ${cid}, ${para}, ${date})"}`;
}

function showCmt(bid, cid, para, date) {
  const { java, cache, source } = this;
  let mname = `qdreader-${bid}-${cid}-${para}`
  let load = (cache.getFromMemory(mname) ?? "-").split("-")
  if (load[0] != 1 || load[1] != date) {
    cache.putMemory(mname, "1-" + date);
    return;
  }

  try {
    let title = cache.getFromMemory(`${mname}-text`) ?? "段评"; java.startBrowser(`{{h}}/dp.php?bookId=${bid}&chapterId=${cid}&paragraphId=${para}`, title);
  } catch (error) {
    java.log("获取或处理评论失败: " + error);
  }
}

