let api = 'https://uaa.vv9v.cn'

function getApiUrl(path, data, _) {
  let { source } = _ || this
  let account_id = String(source.get('account_id')) || '0'
  data.account_id = account_id
  let hostIndex = String(source.get('hostIndex'))
  if (hostIndex) {
    data.hostIndex = hostIndex
  }
  data = Object.entries(data).map(v => v.join('=')).join('&')
  let androidId = String(source.get('androidId'))
  if (!androidId) {
    androidId = _.java.androidId()
    source.put('androidId', androidId)
  }
  let option = {
    method: "GET", headers: {
      'x-sec-token': source.getVariable(),
      'x-android-id': androidId
    }
  }
  let url = api + path + '?' + data + ',' + JSON.stringify(option)
  return url
}

function requestApiUrl(path, data, _) {
  let url = getApiUrl(path, data, _)
  try {
    let res = _.java.ajax(url)
    res = JSON.parse(res)
    if (res.error) {
      _.java.longToast(`\n操作失败，code${res.code}：\n${res.error}`)
    }
    if (res.data) {
      return res.data
    }
  } catch (e) {
    _.java.log(`请求共享接口${path}异常：` + e.message)
  }
  return null
}

let hosts = [
  "uaa010.com",
  "uameta.ai",
  "uaa.com",
  "uaa202410.com",
].map(v => 'https://cdn.' + v + '/file/bucket-media')

function getHost(_) {
  let { source } = _ || this
  let host = source.get('host')
  if (host == '') {
    return hosts[0]
  }
  return host
}

function getFileUrl(path, _) {
  let host = getHost(_)
  let option = {
    method: "GET",
    headers: {
      "User-Agent": "Mozilla/5.0 (Linux; Android 15; V2304A Build/AP3A.240905.015.A2; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/132.0.6834.163 Mobile Safari/537.36",
      "Referer": "uaa.com"
    }
  }
  let url = host + path + ',' + JSON.stringify(option)
  return url
}

