function login() {
  let info = JSON.parse(String(source.getLoginInfo()))
  _login(info)
}

function _login(info) {
  let oriId = String(source.get('account_id'))
  if (oriId !== '' && oriId !== '0') {
    return java.longToast('\n❌登录失败，当前已登录，请先退出登录')
  }
  info = info || result
  java.log(JSON.stringify(info))
  let username = info['账号']
  let password = info['密码']
  if (username == '' || password == '') {
    return java.longToast('\n❌登录失败，用户名或密码不能为空')
  }
  let data = requestApiUrl('/console/login', { username: username, password: password }, this)
  if (!data || !data.id) return
  let { id, nickName, vip } = data
  source.put('account_id', id)
  source.putLoginInfo(JSON.stringify(info))
  java.longToast(`\n✅登录成功：${nickName}\nid：${id}\n会员：${vip ? '是' : '否'}`)
}

function logout() {
  source.put('account_id', '0')
  java.longToast('\n✅退出登录成功，将使用共享账号获取章节')
}

function showUserInfo() {
  let account_id = String(source.get('account_id'))
  if (!account_id || account_id == '0') {
    return java.longToast('\n❌查询失败，未登录账号')
  }
  let data = requestApiUrl('/console/info', { account_id: account_id }, this)
  if (!data || !data.id) return
  java.longToast(`\n✅查询成功：${data.nickName}\nid：${data.id}\n会员：${data.isVip ? '是' : '否'}`)
}

function queryToken() {
  let token = String(source.getVariable())
  if (token.length <= 60) {
    java.toast('请输入有效token')
  } else {
    let info = requestApiUrl('/user/test', { token: token }, this)
    if (!info) return
    source.setVariable(token)
    java.longToast(`\n✅查询成功\n用户：${info.id}${info.max ? `\n日下载上限：${info.max}` : ''}\n今日已下载：${info.used}\n日下载剩余：${info.left}\n创建时间：${info.createTime}${info.ddl ? `\n到期时间：${info.ddlTime}` : ''}`)
    source.putLoginInfo(JSON.stringify(result))
  }
}

function getTempToken() {
  let pw1 = String(result['源作者'])
  if (pw1 == '') {
    return java.longToast('请输入源作者')
  }
  let pw2 = String(result['官方反馈群'])
  if (pw2 == '') {
    return java.longToast('请输入官方反馈群')
  }
  let pw3 = String(result['临时Token口令'])
  if (pw3 == '') {
    return java.longToast('请输入临时Token口令')
  }
  let info = requestApiUrl('/user/temp', { id: java.androidId(), pw1: pw1, pw2: pw2, pw3: pw3 }, this)
  if (!info) return
  let token = info.token
  source.setVariable(token)
  java.longToast(`\n✅获取临时token成功\n用户：${info.id}\n日下载剩余：${info.left}\n创建时间：${info.createTime}\n到期时间：${info.ddlTime}`)
  source.putLoginInfo(JSON.stringify(result))
}

function showDb() {
  let data = requestApiUrl('/system/dbinfo', {}, this)
  if (!data) return
  java.longToast('\n' + data.msg)
}

function changeOrder(type) {
  let order = ''
  if (type == '最新上架') {
    order = "1"
  } else if (type == '最近更新') {
    order = "2"
  } else if (type == '最多观看') {
    order = "3"
  } else if (type == '最多收藏') {
    order = "4"
  }
  source.put('order', order)
  java.longToast('\n✅发现页排序已切换为：' + (type || '默认'))
}

function changeHostIndex(index) {
  if (!index && index !== 0) {
    source.put('hostIndex', '')
    java.longToast('共享api接口已切换为：默认')
  } else {
    source.put('hostIndex', index + '')
    java.longToast('共享api接口已切换为：' + index)
    let data = requestApiUrl('/system/hostping', { hostIndex: index }, this)
    if (!data) return
    java.longToast(`\n接口状态：${data.isAvailable ? '可用' : '不可用'}\n延迟：${data.latency}ms\n质量：${data.rating}`)
  }
}

function changeHost(index) {
  if (index < 0 || index >= hosts.length) {
    throw new Error('Invalid host index')
  }
  let host = hosts[index]
  source.put('host', host)
  java.longToast('UAA文件域名切换为：\n【' + index + '】' + host)
  try {
    let date1 = Date.now()
    let html = java.get(host + 'image/cover/41559536e3e74e0f99f46ff70446cf8b.webp', { 'user-agent': 'Mozilla/5.0 (Linux; Android 15; V2304A Build/AP3A.240905.015.A2; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/132.0.6834.163 Mobile Safari/537.36', "referer": "uaa.com" })
    let code = html.statusCode()
    let date2 = Date.now()
    let t = date2 - date1
    let time = t / 1000 + 's'
    let logTime = '【' + index + '】' + '解析时间：' + time

    if (code < 200 || code >= 400 || t > 5000) {
      java.longToast('\n【访问失败' + code + '】\n' + '┏┅━┅━┅━┅━┅┅━┅━┅┓\n' + logTime + '\n♣️访问失败，可能被墙♣️\n请切换域名/切换网络环境\n┗┅━┅━┅━┅━┅┅━┅━┅┛')
    } else if (t < 1000) {
      java.longToast('\n【速度优良】\n' + '┏┅━┅━┅━┅━┅┅━┅━┅┓\n' + logTime + '\n❤️延迟较低，推荐使用❤️\n网络环境优良，请继续保持状态\n┗┅━┅━┅━┅━┅┅━┅━┅┛')
    } else if (t >= 1000 && t < 2000) {
      java.longToast('\n【速度一般】\n' + '┏┅━┅━┅━┅━┅┅━┅━┅┓\n' + logTime + '\n♦️延迟一般，勉强使用♦️\n可尝试其他域名或切换网络环境\n┗┅━┅━┅━┅━┅┅━┅━┅┛')
    } else if (t >= 2000 && t < 5000) {
      java.longToast('\n【速度堪忧】\n' + '┏┅━┅━┅━┅━┅┅━┅━┅┓\n' + logTime + '\n♣️延迟过高，不建议使用♣️\n请切换其他域名或切换网络环境\n┗┅━┅━┅━┅━┅┅━┅━┅┛')
    }
  } catch (err) {
    java.toast('\n💔访问出错，请换接口💔')
  }
}

