ruleToc.chapterList:
@js:
let novelId = java.hexDecodeToString(result)
let type = JSON.parse(baseUrl.slice(baseUrl.indexOf('{'))).type
result = requestApiUrl(`/${type}/catalog`, { novelId: novelId }, this)
let menus = Array.from(result.menus)

let cache = requestApiUrl(`/${type}/cache`, { novelId: novelId }, this) || []
cache = cache.reduce((acc, d) => {
  acc[d.chapId] = d
  return acc
}, {})

function formatTime(time) {
  return new Date(time).toLocaleString('zh-CN', {
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  })
}

function formatMenus(menus) {
  menus.forEach(c => {
    let cacheChap = cache[c.id]
    let isShare = !!cacheChap
    let tag = isShare ? '🥑' : ''
    let info = ''
    let onlineTime = cacheChap ? cacheChap.onlineTime : formatTime(c.onlineTime)
    if (type == 'novel') {
      info = `${onlineTime} ┃ 字数：${c.volumeNum || 0}`
    } else if (type == 'comic') {
      info = `${onlineTime}${cacheChap ? ` ┃ 图片数：${cacheChap.imageCount || 0}` : ''}`
    } else {
      if (cacheChap) {
        info = `${onlineTime}${cacheChap.duration ? ` ┃ 时长：${cacheChap.durationFormat}` : ''}`
      } else {
        info = onlineTime
      }
    }
    let isVip = c.limit != '游客' && c.limit != '注册会员'
    chaps.push({
      title: isVip ? tag + c.title : c.title,
      info: info + tag,
      isVip: !isShare && isVip,
      // url: getApiUrl(`/${type}/chap`, { novelId: novelId, chapId: c.id })
      url: `data:;base64,${java.base64Encode(c.id)},{"type":"${type}","novelId":"${novelId}"}`
    })
  })
}

let chaps = []
if (type == 'novel') {
  book.type = 8
  menus.forEach(v => {
    chaps.push({
      title: v.title,
      voltype: true
    })
    formatMenus(v.children)
  })
} else if (type == 'comic') {
  book.type = 64
  formatMenus(menus)
} else if (type == 'audio') {
  book.type = 32
  formatMenus(menus)
}
chaps

searchUrl:
@js:
let type = 'novel'
if (key.startsWith('c') || key.startsWith('C')) {
  type = 'comic'
  key = key.slice(1)
} else if (key.startsWith('a') || key.startsWith('A')) {
  type = 'audio'
  key = key.slice(1)
}
getApiUrl(`/${type}/search`, { keyword: key, page: page, size: 20 })

