};
try {
    $$$ = JSON.parse(source.getVariable());
    if ($$$ == null) {
        error;
    } else {
        '';
    };
} catch (e) {
    $$$ = original;
    put($$$);
}
function put(data) {
    return source.setVariable(JSON.stringify(data, null, '\t'));
}
function n(e) {
    n = '\n';
    for (m = 1; m < e; m++) {
        n = n + '\n';
    }
    return n;
}
function log(e) {
    if (e == undefined) {
        return;
    }
    java.longToast(e);
}
function login() {
    result = JSON.parse(source.getLoginInfo());
    var username = result['账号'];
    $$$.name = username;
    var password = result['密码'];
    var options = {
	   body: `username=${username}&password=${password}&submit_login=1`,
	   method: 'POST'
	}
        var urls = java.ajax(`https://jmcomicgo.me`)
	var url = urls.match(/china"><span>(.*?)<\/span>/)[1];
        $$$.url = url;
	java.ajax(`${url}/login,${JSON.stringify(options)}`)
	return put($$$);
	}

P = ['时间','阅读','收藏'];
function typeP(e) {
    if (e == undefined) {
        e = 0;
    }
    return P[e];
}
function tag(e) {
    if (tag == undefined) {
        tag = result.tag;
    }
    if (e == $$$.tag) {
        return log('【标签切换提示】' + n(2) + '已经选择此标签，无需更换');
    }
    log('【标签切换提示】' + n(2) + '已经切换到[' + typeP(e) + ']');
    $$$.tag = e;
    return put($$$);
}

function url() {
    var urls = java.ajax(`https://jmcomicgo.me`)
    var url = urls.match(/china"><span>(.*?)<\/span>/)[1];
    if (url == $$$.url) {
         return log('【站点提示】' + n(2) + '站点无需更换');
    }
    log('站点变更:' + url)
    $$$.url = url;
    return put($$$);
