Packages.cn.hutool.crypto.digest.Digester,
Packages.cn.hutool.core.codec.Base64,
Packages.cn.hutool.crypto.digest.DigestAlgorithm);
with (aly) {
	//转ASCII数组
	let asBs =  Packages.java.lang.String(cv).getBytes("US-ASCII");
