body = Object.keys(data).map(key => `${key}=${data[key]}`).join('&');
//java.log(body)
result = java.ajax(`https://hanmm.org/api/reader_js.php,{"body": "${body}","method": "POST"}`)
if (/[\u4e00-\u9fa5]/.test(result)) {
	result
} else {
	data = JSON.parse(result)
	imageList = data.data.image_list
	imageList.sort((a, b) => a.order - b.order)
	imageList.map(img => {
		return `<img src="${toDataUrl(JSON.stringify(img))}">`
	}).join("\n")
}

ruleContent.imageDecode:
json=java.bytesToStr(result)
//java.log(json)
img=JSON.parse(json)
result=java.hexDecodeToByteArray(java.ajax(`${img.pic},{"type": "jpg","headers": {"Referer": "https://hanmm.org/"}}`))
if (img.arr==""){
	result
}else{	arr=JSON.parse(java.base64Decode(img.arr))
	result=decodeImg(result,arr)
}
result

