H = () => {
  if (source.getVariable() == 0) {
    java.toast('❗小说模式❗');
  } else if (source.getVariable() == 1) {
    java.toast( '❗漫画模式❗');
  }else{java.toast( '❗视频模式❗');
}
}
A = (x) => {
  source.setVariable(x);
  if (x === 0) {
    java.toast('成功设置'+'\n' + '❗小说模式❗');
  }else if(x==1){
  	java.toast('成功设置'+'\n' + '❗漫画模式❗');
}else{java.toast('成功设置'+'\n' + '❗视频模式❗');
}
}

