var booktype=book.tocUrl.match(/net\/([^/]+)\/.+/)[1];
if (booktype=="novel"){
textv=org.jsoup.Jsoup.parse(result).select('main').html();
}else if (booktype=="comic"){
textv=org.jsoup.Jsoup.parse(result).select('main').html();
}else{
textv=result.match(/[\s\S]*data-url\="([^"]+)"[\s\S]*/)[1];
var html = `data:text/html;charset=utf-8,<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
</head>

<body>
    <video controls
        src="`+textv+`"> width="100%" height="285px" controls="" autoplay=""
    </video>

    <script src="https://cdn.jsdelivr.net/npm/hls.js@latest"></script>
  </body>

</html>`;
java.startBrowser(html,'加载可能有些慢，网站上也是如此，请耐心等待');
	}
textv;

